css:
@-moz-document url("https://www.google.com/"), url("https://www.google.de/"), url-prefix("https://www.google.com/webhp"), url-prefix("https://www.google.com/imghp"), url-prefix("https://www.google.de/webhp"), url-prefix("https://www.google.de/imghp"), url-prefix("https://www.google.com/?safe=active&ssui=on"), url-prefix("https://www.google.com/search?") {
/* change Google main page background and stretch it to 100% width */
body 
{ 
    background-image:url(https://d.facdn.net/art/aotoaka/1496413238/1496413238.aotoaka_tom.jpg) !important; 
    background-size:100% auto !important;
}

/* make Google search results background white, so it doesn't use the custom background */
div#cnt 
{ 
    background-image:url(https://d.facdn.net/art/aotoaka/1496413238/1496413238.aotoaka_tom.jpg) !important; 
    background-size:100% auto !important;
}

/* replace original Google logo with a translucent white logo */
div#lga 
{
    background-image:url(https://lh3.googleusercontent.com/4R1RkG7jUVDKFrrUcH3P96Q9o-qy__hiZCLDukCpvc5oeslM-JI4uH22SJol0Lk_2CkszcQ4rlR67vF7UMf4=w811-h621) !important;
      background-size: contain;
     text-indent: 100%;
    background-repeat:no-repeat !important;
    background-position: center center !important;
}

/* hide the original colorful logo */
#hplogo { 
    display:none !important 
}

/* make footer links, image search text, user account name white with some shadow */
div#footer a, span.sshpplo, span.sshpplo a, div.sshpplo, div.sshpplt a, span#gbgs4dn
{ 
    color:#ddd !important;
    text-shadow: 0.1em 0.1em 0.2em #1b1b1b !important;
}
#fbar {
    background:none !important;
    border:none !important;
}
}
