body
{
    font-family: 'Open Sans', sans-serif;
    margin:0 auto;
    padding:15px;
    background-image: url("https://i.imgur.com/Nl6St4S.png");
    background-repeat: repeat;
     cursor:url('http://78.media.tumblr.com/def8729b4eaed6fe2ae6dd6b5e52f2ad/tumblr_inline_nrkbg8htJ11ry72eo_75sq.png') 0 0, auto;
}

#wrapper
{
    position: relative;
    overflow:hidden;
}

a
{
    text-decoration:none;
}

a:hover
{
    text-decoration:none;
    color:inherit;
}

img
{
    image-rendering: optimizeQuality;
    -ms-interpolation-mode: bicubic;
    border:none;
}

h1
{
    margin:0;
    font-size:25px;
    font-weight:normal;
    overflow:hidden;
}

h2
{
    margin:0;
    font-size:16px;
}

form
{
    display:inline;
}

.fluid-ratio-resize { 
    display:none;
}

.center {
    text-align:center;
}

.clearer
{
    clear:both;
}

.clearer_r
{
    clear:right;
}

.clearer_l
{
    clear:left;
}

.fright
{
    float:right !important;
}

.fleft
{
    float:left !important;
}

.dnone {
    display:none !important;
}

.nooverflow {
    overflow:visible !important;
}

.placeholder {
    color:#999;
}

b, strong { 
    font-weight: bold;
    color: #47B6FF;
    letter-spacing: 1px;
}

#content
{
    padding: 0 0px 10px;
    margin:10px auto 0px;
    color: #b90000;
}

#header
{
    text-align:center;
}

#header a
{
    color:inherit;
}

#header #title
{
    font-weight:bold;
}

#header #title:hover
{
    text-decoration:none;
}

#footer
{
    max-width: 1000px;
    min-height:48px;
    color: #47B6FF;
    margin:0 auto;
}

#footer .text
{
    font-size:11px;
    text-align:center;
}

#footer>*>div{
    float: left;
}

#footer>*>.cp_link{
    margin-top:30px;
}

#footer>*>.cp_link>a{
    color:#47B6FF;
}

#footer>*>.cp_link>img{
}

.clearfooter
{
    height:55px;
    clear:both;
}

#navig
{
    float:right;
    margin-left:100px;
}

#navig a
{
    display:block;
    padding: 5px 8px;
}

#navig ul
{
    list-style:none;
    padding: 0;
    margin: 0;
    display:block;
}

#navig ul li
{
    float:left;
    margin: 4px 0 4px 7px;
}

#navig ul li ul
{
}

#navig ul li ul li
{
}

.panel
{
    margin:0px 10px 5px 10px;
    color: darkslateblue;
    max-width:1000px;
    margin: 0 auto;
    font-size:14px;
    background-color: white;
}

.panel a
{
    color:inherit;
}

.panel a:hover
{

}


.list
{
    margin: 0px;
    overflow:hidden;
    z-index:-1;
    padding:6px 5px;
    color: black;
    background-color: white;
}

.list > .title, .large > .title
{
    margin: -7px -7px -10px -10px;
    position: relative;
    font-size: 30px;
    padding-bottom: 0px;
    color: white;
    padding-left: 15px;
    background: darkslateblue;
        font-weight: normal;
    text-shadow: 0px 0px 0px #fff7ed;
}

.list > .title.fleft, .large > .title.fleft, .large >.title.fright
{
    margin-right:10px;
    border:none;
    padding:0px;
    margin-bottom:0;
}

.list .group
{
    margin-top:8px;
    padding:0 2px 5px 5px;
    position:relative;
    overflow:hidden;
}

.list .group > .title
{
    color: #286298;
    font-weight:bold;
    font-size:16px;
    padding-bottom: 5px;
    letter-spacing: 1px;
}

.list .group > .title > .meta
{
    font-weight:normal;
    font-size:11px;
}

.list .element
{
    position:relative;
    padding: 3px 10px 3px 10px;
    overflow:hidden;
}

.list .element .title {
    float:left;
}

.list .element .meta_r
{
    float:right;
    font-size: 11px;
    margin-top:2px;
}

.list .element .meta_r a
{
    font-size:120%;
}

.list .group .preview {
    float:left;
    max-height:125px;
    margin-right:5px;
    position:relative;
    z-index:100;
}

.list.series .group {
    min-height:70px;
    padding: 0 8px 8px;
}

.list.series .meta_r {
    position:static;
    float:none;
    clear:both;
}

.list .element .image {
    float:left;
    max-height:125px;
    margin-right:5px;
    position:relative;
    z-index:100;
}



.large
{
    padding:10px;
    margin:0 0 10px;
    background-color:#fffffff;
    overflow:hidden;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
    z-index:-1;
}

.large.comic.alert
{
    color:#3a4246;
}

.large.comic .title
{
    font-size:20px;
}

.thumbnail
{
    padding: 3px;
    text-align:left;
}

.thumbnail img {
    width:200px;
    height:280px;
    border: 1px solid #343434;
    box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -webkit-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -moz-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
}

.large.comic .thumbnail
{
    padding:3px;
    text-align:left;
    float:left;
    padding-right:20px;
    oadd-bottom:20px;
}

.large.comic .thumbnail img
{

}

.large.comic .info
{
    text-align:justify;
}

.large.comic .info ul
{
    list-style: none;
    padding:0 0 0 15px;
}

.large.comic .info ul li
{
    margin: 6px 0 0;
    text-indent:-13px;
}


.sidebar
{
    float:right;
    margin:0 0 10px 10px;
    padding:0;
    width:auto;
    z-index:-1;
    list-style: none;
}


.sidebar li
{
    margin: 0 0 10px;
    padding:10px;
    border-top: solid 2px;
    border-right: solid 2px;
}

.sidebar li h3
{
    position:relative;
    font-weight:bold;
    font-size:16px;
    margin-top: 0;

    background: darkslateblue;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    letter-spacing: 2px;
        text-shadow: 0px 0px 0px rgba(0, 0, 0, 0);

}

.sidebar .text
{
    padding: 0 5px;
}

#page
{
    margin:10px auto;
    overflow:hidden;
    max-width:1000px;
    padding: 5px 0;
    position:relative;
    box-shadow: inset 0px 0px 3px #fffff;
    background-color:#fffffff;
}

#page .inner
{
    margin: 0 auto;
    position:relative;
    text-align:center;
    background-color:#fffffff;
}

#page .inner > a > img
{
    margin:0 auto;
    display:block;
    z-index:1000;
    background-color:#fffffff;
}

#page .inner .preview
{
    width:100%;
    position:absolute;
    top:3px;
    z-index:-1;
    background-color:#fffffff;
}

#page .inner .preview > img
{
    background-color:#fffffff;
}

#pagelist
{
    margin: 0 auto;
}

#pagelist .title
{
    padding:5px;
    margin:0 5px;
    border-bottom:1px solid #ffffff;
}

#pagelist .images
{
    width:98%;
    margin:5px auto;
    overflow-x:hidden;
}

#pagelist .images table
{
    width:950px;
    padding:0;
    margin:0;
}

#pagelist .images table tr
{
    height:100px;
    width:90%;
}

#pagelist td
{
    border: 2px solid transparent;
    width:150px;
    height:170px;
    margin: 0 5px 5px 0;
    text-align:center;
    vertical-align:middle;
    background-color:#fffffff;
}

#pagelist a
{
    width:160px;
    display:block;
    text-decoration:none;
}

#pagelist a img
{
    max-height:160px;
    max-width:160px;
}

#pagelist a img.loaded
{
    border: 2px solid #fffffff;
}

#pagelist a img.current
{
    border: 2px solid #ffffff;
}

.ads{
    background-color:#fffffff;
}

.ads a{
    background-color:#fffffff;
}

.ads a:hover{
    background-color:#fffffff;
}

.ads.banner
{
    margin: 0px auto 10px;
    padding:7px 10px;
    overflow:hidden;
    text-align:center;
    color:#508dc6;
    background-color:#fffffff;
    box-shadow: 0px 0px 1px #ffff, inset 0px 0px 8px #fffffff;
    -webkit-box-shadow: 0px 0px 1px #ffff, inset 0px 0px 8px #fffffff;
    -moz-box-shadow: 0px 0px 1px #fff, inset 0px 0px 8px #fffffff;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
    clear:both;
    width:980px;
}

.ads.banner *
{
    margin: 0 auto;
    background-color:#fffffff;
}

.ads.vertical
{
    margin:0 10px;
    padding: 7px 7px;
    background-color:#fffffff;
}

.ads.iframe iframe
{
    border:none;
    background-color:#fffffff;
}


.ads.banner.iframe iframe{
    height:90px;
    width:728px;
    background-color:#fffffff;
}

.ads.banner.iframe {
    background-color:#fffffff;
}

/**********
*Scroll*
**********/

*:before,
*:after {
  box-sizing: border-box;
}

header {
  text-align: center;
  font-size: 25px;
  text-transform: uppercase;
  background: rgba(58, 66, 70, 0.8);
  padding: 20px;
}

header p {
  margin: 0px;
}

header p:last-child {
  color: #f0f0f0;
}

.main {
  width: 760px;
  margin: 0 auto;
  background: rgba(58, 66, 70, 0.8);
  position: relative!important;
  padding: 20px;
}

.left,
.right {
  float: left;
  display: block;
}

.left {
  width: 200px;
  padding: 10px;
}

.left ul {
  position: fixed;
  display: block;
  background: rgba(58, 66, 70, 0.8);
  padding: 0px;
}

.left ul li {
  list-style: none;
  line-height: 30px;
}

.left ul li a {
  color: #f0f0f0;
  display: block;
  padding: 5px;
  text-align: center;
  text-decoration: none;
  width: 190px;
}

.left ul li a:hover {
  background: rgba(58, 66, 70, 0.8);
}

.right {
  width: 520px;
  padding: 10px;
}

.right > p {
  margin-bottom: 40px;
}

#h1 {
  background: rgba(58, 66, 70, 0.8);
  color: #f0f0f0;
  padding: 5px 10px;
}

#h2 {
  background: rgba(58, 66, 70, 0.8);
  color: #f0f0f0;
  padding: 5px 10px;
}

#h3 {
  background: rgba(58, 66, 70, 0.8);
  color: #f0f0f0;
  padding: 5px 10px;
}

footer {
  clear: both;
}

.scrollTop {
  position: fixed;
  right: 5%;
  bottom: 10px;
  background: rgba(58, 66, 70, 0.8);
  padding: 20px;
  opacity: 0;
  transition: all 0.4s ease-in-out 0s;
  font-family: 'Open Sans', sans-serif;
  font-weight: bold;
}

.scrollTop a {
  font-size: 18px;
  color: #f0f0f0;
}

/***********
**Top Bar***
***********/

.topbar{
    padding:3px 5px 10px;
    margin:0 0 10px;
    text-shadow:none;
    position:relative;
    color:#F6CDDE;
}

.topbar_left{
    float:left;
    text-align:left;
}

.topbar_left>*{
    float:left;
    text-align:left;
    margin-left:5px;
}

.topbar_right{
    float:right;
    text-align:left;
}

.topbar_right div{
    float:right;
    text-align:left;
    margin-right:5px;
}

.topbar>div{
    margin-top:4px;
    padding:5px;
}

.divider
{
    background-color:#C4E9FC;
    height:30px;
    width:1px
}

input
{
    border: 1px solid #343434;
    box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -webkit-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -moz-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
    padding:5px;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-weight: bold;
}

/**********
*Drop Down*
**********/

.prevnext {
    margin-top: 20px;
