    -moz-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
    text-decoration: none;
}

.prevnext .gbutton a:hover, .pagination .active a {
}

.dropdown_parent
{
    position:relative;
    display:block;
    z-index:2000;
    line-height:1.7;
    border: 1px solid #343434;
    box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -webkit-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    -moz-box-shadow: 0px 0px 1px #343434, inset 0px 0px 8px #777;
    border-radius:5px;
    -moz-border-radius:5px;
    -webkit-border-radius:5px;
}

.dropdown_parent .text
{
    padding:6px;
    font-size:14px;
    overflow:hidden;
    line-height:1.7;
}

.dropdown_parent:hover .text{
    position:relative;
    line-height:1.7;
}

.dropdown_parent:hover ul.dropdown
{
    display:block;
    line-height:1.7;
    background: rgba(58, 66, 70, 0.8)
}

ul.dropdown
{
    display:none;
    color:#C4E9FC;
    font-size:14px;
    position:absolute;
    top:16px;
    line-height:120%;
    width:260px;
    max-height:400px;
    overflow:auto;
    overflow-x:hidden;
    z-index:1200;
    padding: 5px;
    list-style: none;
    line-height:1.5;
}

.dropdown_right ul.dropdown
{
    line-height:1.7;
}

ul.dropdown li
{
    line-height:1.7;
}

ul.dropdown li a
{
    display:block;
    width:100%;
    height:100%;
    line-height:1.7;
}
.dh {
