/*************
*Page Numbers*
*************/

.topbar_right .number
{
    text-shadow:none;
    border-radius:4px;
    -webkit-border-radius:4px;
    -moz-border-radius:4px;
    text-align:center;
    font-size:14px;
    font-family: 'Roboto Slab', serif;
    border: 1px dashed #ffffff;
    background-color:#fffffff;
}

.topbar_right .number a
{
    display:block;
    padding:6px;
    color:inherit;
    background-color:#fffffff;
}

.topbar_right .number.current_page
{
    border:1px solid lightgray !important;
    background-color:#fffffff;
}

.topbar_right .number.loaded
{
    border:1px solid #ffff;
    background-color:#fffffff;
}

.topbar_right .number:hover
{
    border:1px solid #ffff;
    background-color:#fffffff;
}

.tbtitle{
    font-size:16px;
    background-color:#fffffff;
}

/********
**Icon***
********/

.icon_wrapper {
    position:relative;
    height:32px;
    width:32px;
    padding:0px;
    text-align:center;
    border-radius:4px;
    -moz-border-radius:4px;
    -webkit-border-radius:4px;
    overflow:hidden;
    cursor:pointer;
}

.icon_wrapper .icon {
    position:absolute;
    top:0px;
    text-align:center;
    top:-9px;
    left:-11px;
}

.topbar>*>.icon_wrapper{
    margin-top:4px;
    left:-15px !important;
}

.icon_wrapper .icon.on {
    display:none;
}

.icon_wrapper:hover .icon.off {
    display:none;
}

.icon_wrapper:hover .icon.on {
    display:block;
}

.icon_wrapper.small {
    width:16px;
    height:16px;
    margin-right:0px;
}

.icon_wrapper.small .icon {
    top:-4px;
    right:4px;
    width:26px;
    height:26px;
}

.icon_wrapper{
    overflow:visible;
}


