css:
@-moz-document domain("drooodle.com") {
body {
    background-image: url(https://i.imgur.com/BlvY7tJ.jpg);
    background-size: 215px 215px;
}

.navbar .center-nav .inactive a {
    color: #2165a5;
}

.navbar .center-nav .active a {
    cursor: default;
    color: #2b8eeb;
}

.navbar {
    background-color: #000;
}

.navbar .center-nav .latest.active a:after {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url("https://i.imgur.com/tZykHIg.png") no-repeat;
    width: 57px;
    /* Width of new image */
    
    height: 5px;
    /* Height of new image */
    
    padding-left: 5000px;
    /* Equal to width of new image */
}

.droodle-list .droodle-item .info-top .half {
    background-color: #000000b3;
}

.droodle-list .droodle-item .info-top .half .creator {
    color: #28abe2;
}

.droodle-list .droodle-item .info-top .half .droodle-created {
    overflow: auto;
    color: #2e83a7;
}

.droodle-list .droodle-item .info-top .droodle-updated {
    overflow: auto;
    color: #2e83a7;
}

.droodle-list .droodle-item .info-top .drooodle-comment span {
    color: #2b8eeb;
    background: #000000;
}

.droodle-control-bar {
    color: #28abe2;
    background: #000000b3;
}

.droodle-info-bottom {
    background: #000000b3;
    color: #28abe2;
}

img[src*="/img/drooodle-logo.svg"] {
    content: url("https://i.imgur.com/AI4g69q.png");
}

.user-hero .user-info {
    background-image: url(https://i.imgur.com/OovjnD4.png);
    background-size: 900px;
}

.navbar-mobile .navbar-toggle button .icon-bar {
    background-color: #2b8eeb;
}

.navbar-mobile {
    background-color: #000;
}

.user-hero .user-info span {
    background-color: #000;
}

.droodle-control-bar .vote-segment .up {
    background-image: url(https://i.imgur.com/KYMQSCe.png)
}

.droodle-control-bar .vote-segment .down {
    background-image: url(https://i.imgur.com/Lgn6M0j.png)
}

.droodle-control-bar .right-options span.more-options {
    background-image: url(https://i.imgur.com/okQ4U55.png)
}

.slider-tool {
    background-image: url(https://i.imgur.com/BlvY7tJ.jpg);
    background-size: 215px;
}

.slider-tool.opacity .rc-slider .rc-slider-handle {
    background: #2b8eeb url(https://www.drooodle.com/img/icon-adjustment-opacity.png) center center no-repeat;
    background-size: 90% auto;
}

.rc-slider .rc-slider-handle {
    position: absolute;
    cursor: default;
    border: 0;
    background: #2b8eeb url(https://www.drooodle.com/img/icon-adjustment-brushsize.png) center center no-repeat;
    background-size: 80% auto;
    box-shadow: 0 2px 0 0 rgba(0,0,0,0.1);
    border-radius: 4px;
}

.tools ul {
    background: #2b8eeb;
}

.tools.vertical .blur-tool.active, .tools.vertical .draw-tool.active, .tools.vertical .line-tool.active, .tools.vertical .undo.active, .tools.vertical .clear.active, .tools.vertical .fill.active, .tools.vertical .text-tool.active, .tools.vertical .image-tool.active {
    background-color: #2e83a7;
}

.colors.vertical {
    background: #2b8eeb;
}

.drawing-tool .description-options .form-group {
    background: #2b8eeb;
}

label {
    display: inline-block;
    margin-bottom: 5px;
    font-weight: 700;
    color: #000000;
    font-size: 13px;
}

.drawing-tool .description-options .form-group input[type="text"] {
    background: #1b1b1b;
    border: 0;
}

.form-control {
    color: #2b8eeb;
}
}
