/*

       The Catacombs Black
              -v1.7 by equinox

       This style uses a shitload of base64 encoded images, enjoy.
       To adjust the font size look under the text section

       v1.7-
            Now working with main page

       v1.6-
            fixed images. rasp is fucking up.


*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thecatacombs.net") {
    
    
    /*============================================
    
        TEXT
        
        ============================================*/

    *
    {
        font-family:Calibri !important;
        /* increase for larger fonts, default=97.5% */
        font-size: 98% !important
    } 

    input,textarea,.imagebutton
    {
        font-size: 110% !important
    }

    pre.alt2 
    {
        font-family:Consolas !important
    }

    a
    {
        text-decoration: none !important;
        color: #858585 !important
    }
    
    a:hover
    {
        text-decoration: underline !important;
        color: #fff !important
    }
    
    font[color="red"], font[color="#FF0000"]
    {
        color: #be0000 !important
    }
    
    font[color="blue"]
    {
        color: #0030BB !important
    }
    
    /*============================================
     
        REMOVALS
        
        ============================================*/

    #AutoNumber1,#AutoNumber2,#AutoNumber3,#AutoNumber7, #qrform table, img[src="http://forums.thecatacombs.net/cats_green/quickreply.gif"], img[src="http://forums.thecatacombs.net/cats_green/collapse_tcat_collapsed.gif"],  img[src="http://forums.thecatacombs.net/cats_green/collapse_tcat.gif"], img[src="cats_green/collapse_thead_collapsed.gif"],  img[src="http://forums.thecatacombs.net/cats_green/collapse_thead.gif"]  
    {
        display:none !important
    }
    
    /*============================================
     
        FORUM STYLES
        
        ============================================*/

    #vB_Editor_QR_controls td,#vB_Editor_001_controls td 
    {
        background-color:#e1e1e2 !important
    }
    
    td.alt1,td.thead 
    {
        border: none !important;
    }
    
    td.alt2 
    {
        border-bottom:none !important;
        border-left:none !important;
        border-right:1px solid #000 !important;
        border-top:none !important
    }
    
    .alt1Active,.alt1,.alt1 td,.panelsurround 
    {
        background-color:#080809 !important
    }
    
    .alt2 
    {
        background-color:#080909 !important
    }
    
    .tborder,table.fieldset
    {
        background:transparent !important;
        border:1px solid #000 !important
    }
    
    .thead,.tcat,.tfoot,.vbmenu_control 
    {
        background:#1F1E1C !important
    }
    
    .vBulletin_editor,fieldset,.fieldset 
    {
        background-color:#0E0D0C !important;
        border:1px solid #000 !important;
        padding:0 !important
    }
    body, div.page, table.page
    {
        background-color: #000 !important
    }
    
    #inlinemodform table, table.tborder
    {
        margin: 0 !important;
        padding: 0 !important
    }
    
    div.page table.tborder,  .page div div table
    {
        background-color:#18191A !important;
    }
    
    /* quotes ( LOL FUCK VBULLETIN ) */
    div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div table tbody tr td.alt2 
    {
        font-size: 110% !important;
        background-color:#0E0D0C !important;
        border:1px solid #000 !important
    }
    
    fieldset td,#vB_Editor_001 td.controlbar 
    {
        background-color:#0E0D0C !important
    }
    
    hr 
    {
        background-color:#000 !important;
        border:0 !important;
        color:#000 !important;
        height:1px !important
    }
    
    #collapseobj_quickreply div.panel,td.panelsurround div.panel,.fieldset td,.panelsurround table.panel 
    {
        background-color:transparent !important;
        border:none !important
    }
    
    
    /*============================================
     
        IMAGES (rofl)
        
        ============================================*/

    img[src="http://forums.thecatacombs.net/cats_green/lastpost.gif"], img[src="http://forums.thecatacombs.net/cats_green/misc/navbits_finallink_ltr.gif"], img[src="cats_green/misc/stats.gif"], img[src="cats_green/misc/whos_online.gif"]
    {
        height:14px!important;
        padding-left:14px!important;
        width:0!important;
    
        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/newthread.gif"] 
    { 
        height:20px!important;
        padding-left:60px!important;
        width:0!important;
        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/firstnew.gif"]
    { 
        height:13px!important;
        padding-left:13px!important;
        width:0!important;
        
        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/reply.gif"] 
    { 
        height:20px!important;
        padding-left:60px!important;
        width:0!important;
        
        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/quote.gif"] 
    { 
        height:20px!important;
        padding-left:44px!important;
        width:0!important;

        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/edit.gif"] 
    { 
        height:20px!important;
        padding-left:44px!important;
        width:0!important;

        background-image:url(data:image/png;base64,) !important;
    }

    img[src="http://forums.thecatacombs.net/cats_green/forum_new.gif"], img[src="http://forums.thecatacombs.net/cats_green/misc/navbits_start.gif"]  
    {
        height:15px!important;
        padding-left:15px!important;
        width:0!important;

        background-image:url(data:image/gif;base64,) !important;
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/forum_old.gif"] 
    {
        height:15px!important;
        padding-left:15px!important;
        width:0!important;

        background-image:url(data:image/gif;base64,) !important;	
    }
    
    img[src="http://forums.thecatacombs.net/cats_green/forum_old_lock.gif"] 
    {
        height:15px!important;
        padding-left:15px!important;
        width:0!important;

        background-image:url(data:image/gif;base64,) !important;
    }

}
