css:
@-moz-document domain("ancestry.co.uk") {


#HeaderRegion {
    background-color: #383838;
}
h6.nodeTitle {
    font-size: 1.4rem!important;
}
.pageHeader.bgColor1~.pagePadded, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .openedNav .tNavWrap {
    background-color: #343b42!important
        ;
}
.treeViewerAwesome .line {
    background-color: #bb9e2b;
}
.treeViewerAwesome .nodePhoto.icon {
    background-color: #343b42;
    color: #abaaaa;
    font-size: 140px;
    position: relative;
    border: 6px inset #646960;
}
.treeViewerAwesome .nodePhoto.iconMale {
    background-color: #3a494c;
    color: #68c2c2;
        border: 6px inset #5c8080;
}
.treeViewerAwesome .nodePhoto.iconFemale {
    background-color: #584343;
    color: #f58888;
    border: 6px inset #886969;

}
.treeViewerAwesome .nodeFamily.focus .nodeContent {
    background-color: #004e54;
    color: #c1beb2;
    border-right: 6px inset #615959!important;
    border-bottom: 6px inset #292626!important;
    border-left: 6px inset #557082!important;
}

.treeViewerAwesome .nodeFamily.add .node-bdy {
    border: 4px dashed #19efca;
}
    .treeViewerAwesome .nodeContent {
border-bottom: 6px inset #004e54;
    border-left: 6px inset #948a8a;
    border-right: 6px inset #828181;
}

.treeViewerAwesome .nodeFamily {
    height: 290px;
    width: 190px;
 }
div.nodePhoto {

    border: 6px inset #848484;
}

.treeViewerAwesome .nodeFamily:not(.add).nodeActive .node-bdy, .treeViewerAwesome .nodeFamily:not(.add):not(.nodeActive) .node-bdy:hover {
    background-color: #a9963a;
}
div#trHover {
    background: #dcd2b5!important;
    border: 6px inset #dfdce0;
}
.ancBtn.silver {
    background: rgb(109, 109, 108);
}
.ancBtn.silver {
    background: rgb(109, 109, 108);
}
.ancBtn.silver:active, .ancBtn.silver.active {
    background: rgba(60, 57, 57, 0.9);
}
.userCardImg.iconMale {
    background-color: #5e6b6f;
}
.userCardImg.iconFemale {
    background-color: #6b5f64;
}
.bgColor1 {
    background-color: #43464e;
}
.personViewPages .personPage {
    padding: 20px;
    background: #c1c1c1;
}
.personViewPages>.tabContent {
    min-height: 345px;
    background: #a3a4a5;
}
.personView .tabsWrapper {
    background-color: #ab8d70;
    padding: 10px 10px 0;
}
html {
    background-color: #43464e;
}
.card:not([class*="bgColor"]):not(.cardEmpty), .card[class*="cardOutline"]:not(.cardEmpty) {
    background-color: #d6d6d6;
}
.personPageFacts .factsSectionFacts .researchList>li>.card:not(.cardHasSelectAction):not(.cardEmpty) {
    background-color: #d8d1bd;
}
.cardInfo {
    color: #b30000;
}
.treeViewerAwesome :focus {
    outline-color: transparent!important;
    box-shadow: 0px 10px 50px 8px #d6e80c5e;
}

#FooterRegion {
    background-color: #616265;
}
.pageHeader.bgColor1 {
    background-color: rgba(51, 65, 70, 0.8)!important;

}
.ancBtn.silver {
    background: rgb(97, 97, 95);
}

}
