css:
@-moz-document domain("balkan-samp.com") {
body {
    background: url("https://i.imgur.com/YHuKO6v.jpg");
    font: 12px/130% Tahoma, sans-serif;
    margin: 0;
    padding: 0
}
#toolbar{
    height: 36px;
    background: #40444e;
    border-top: solid 1px #606060;   
}
#header-tab{
    background: #222;
    height: 25px;
    border-bottom: solid 1px #606060;   
}
h3.catbg{
    background: #40444e;
    padding-right: 9px;  
}
div.cat_bar{
    background: #40444e;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;  
    border-radius: 0px 0px 10px 10px;
}
#header{
    position: relative;
    height: 120px;
    overflow: hidden;
    background: url("https://i.imgur.com/ps7AbGj.png") repeat-x;
}
.avatar{
    border-radius: 50px 0;
    border: 2px solid #ff9900;
}
#footer{
    padding: 20px 40px 20px 40px;
    font-size: 14px;
    color: #000;
    border-top: solid 1px #606060;
    text-align: center;
    background: #40444e;
    border-radius: 0px 0px 30px 30px;
}
div.title_barIC{
    background: #40444e;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
    border-radius: 0px 0px 10px 10px;
}
div.title_barIC h4.titlebg{
    background: #40444e;
}
.poster h4, .poster ul li{
    background: none !important;
    border: none;
    margin-bottom: 2px;
    padding-top: 4px;
    padding-bottom: 4px;
    width: 95%;
    overflow: hidden;
}
li.postgroup{
    background: #40444e;
    border-radius: 0px 0px 10px 10px;
}
.poster{
    float: left;
    padding-left: 8px;
    width: 220px;
    box-shadow: 1px 1px 1px 2px silver;
    background: url(https://i.imgur.com/8IrQ621.png) no-repeat;
    background-size: auto;   
}

.description, .description_board, .plainbox{
    padding: 0.5em 1em;
    font-size: 0.9em;
    line-height: 1.4em;
    border: 1px solid #bbb;
    background: #40444e;
    margin: 0.2em 1px 1em 1px;
    color: #fff;   
}
#userinfo {
    margin: 10px;
    font-size: 11px;
    color: #fff;
    float: right;
    padding: 12px 12px;
    text-align: right;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid #222;
    font-weight: bold;
    text-shadow: 2px 2px 2px black;
    border-radius: 0 0 0 25px;
}
#userinfo a:link, #userinfo a:visited {
    color: #ffa722;
    font-weight: bold;
}
a img {
    border: 0;
    border-radius: 10px;
}
.poster h4, .poster h4 a {
    font-style: italic;
}
.postarea, .moderatorbar {
    margin: 0 0 0 250px;
}
#post_header dt, .postbox dt {
    float: left;
    padding: 5px;
    width: 15%;
    margin: .5em 0 0 0;
    font-weight: bold;
}
input, button, select, textarea {
    font: 95%/115% verdana, Helvetica, sans-serif;
    color: #000;
    background: #fff;
    border: 1px solid #7f9db9;
    padding: 6px;
    border-radius: 10px;
}
.button_submit, .button_reset {
   cursor: pointer;
    background: #f27d00;
    color: #FFF;
    padding: 10px 20px;
    border-radius: 3px;
    font-size: 12px;
    line-height: 12px;
    font-weight: bold;
    border: none;
    
    
}
.button_submit:hover, .button_reset:hover{
 	color: #FFF;
    background: #e77700;
}

input:hover, textarea:hover, button:hover, select:hover{
 	border: none !important;   
}


}
