css:
@-moz-document url("https://torrentfreak.com/"), url-prefix("https://disqus.com/embed/comments/?base=default&f=torrentfreak"), domain("torrentfreak.com") {
.menu__link::before, .menu__list .sub-menu li a, .article__body table thead, .widget-slider, .tool-pagination > ul li:not(.navigation__item) .current, .comments, .preview-article, body.dark, .pbx.comments, .entry-kilo .entry-title-wrapper, .entry-micro .entry-title-wrapper, .entry-nano .entry-title-wrapper, #feature-box .entry-pico, .entry-title, #main{background: #1C1C1C !important;}

.footer__title, .stats__value, .bread_cr, div.category_description p, .tf-content, .article__body table thead, .comments__message span, .page__heading span, .hero__title span, .stats__description, a, .navigation__link, .o-comments--white, .hero__published, .hero__title, .preview-article > a, html, .notification-count, .dark .tab-conversation.active > a, .dark .nav-tab > a, #sidebar, .post-content .post-message p, .dark .icon-cog, .dark .icon-export, .dark .label, .share-bar .thread-share-bar-buttons .thread-share__button, .input--text:placeholder-shown, .placeholder, .textarea, .comments-text, .pbx-title, .stats-number, .entry-title, .entry-lead, .entry-summary, .entry, .hentry{color: #AAA !important;}


.tf-logo, .menu__link--active, .menu__list .sub-menu li a:hover, .menu__list .sub-menu li a:active, .menu__list .sub-menu li a:focus{background: #444 !important;}


.o-widget__title, .preview-article__category, .unread .notification-icon{color: #444 !important;}


.o-widget__title, .logo, .preview-article__category::before, .preview-article__category{background-color: #AAA !important;}

.footer, .header__fixed{border-color: #AAA !important;}

#wrap-page{background: none !important;}

.tf-content, .tool-pagination > ul li:not(.navigation__item) a, .container.container--main::after, .page__content, .share-bar .thread-share-bar-buttons .share-twitter, .share-bar .thread-share-bar-buttons .share-facebook, .dark .input--text, .textarea-wrapper, body, html{background: #141414 !important;}

.entry-kilo, .entry-micro, .entry-nano{border-bottom: 2px solid #1C1C1C !important;}

#cf-content > h1:first-of-type, .tf-error > div > h1:first-child, .tf-header, .tf-content, .preview-article, .comments, .dark .input--text, .textarea-wrapper{border-color: #777 !important;}

.auth-disqus button, .auth-facebook button, .auth-google button, .auth-twitter button{filter: grayscale(1) !important;}

.logo__image {-webkit-filter: invert(0.88) grayscale(1)  brightness(1.5) !important;}

.tf-logo-img, .comments__icon, .navigation__link--left img, .navigation__link--right img, .widget-slider__navigation svg, .widget-slider .cycle-pager, .widget-commented__counter, .o-comments::before{-webkit-filter: invert(0.88) grayscale(1)  brightness(1.5) !important;}

.o-widget, .widget-slider{border: 100px #777 !important;}

.o-widget--rotated{transform: none !important;}

.widget-slider{box-shadow: none !important;}
}
