css:
@-moz-document domain(autosport.com) {
div#scrbbl-holder-stickies {
    position: fixed;
    top: 10px;
}
div#main-menu,
.grid.live,
div#livenews,
.MPU_container,
div#footer {
    display: none !important;
}
div#article {
    padding: 0;
}
.live-update:first-child {
    border-top: 0 !important;
}
.liveupdate {
    border-top: 0;
    border-bottom-width: 1px;
    border-bottom-color: #c3cdd3;
    border-bottom-style: dotted;
    padding: 5px 0 0;
    margin: 0;
}
}
