css:
@-moz-document domain("www.acfun.cn") {
  

body {
    font-family: "AcFun Symbol","Helvetica Neue",Helvetica,Arial,"pingfang SC","Microsoft Yahei",STHeiti,sans-serif;
    font-size: 12px;
    color: #666;
    line-height: 1.6;
    background: #C8E4C8 !important;
}

.header .header-top {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background: #C8E4C8 !important;
    height: 46px;
    font-size: 12px;
    box-sizing: border-box;
    z-index: 10;
}

.header .nav {
    top: 180px;
    z-index: 9;
    height: 46px;
    line-height: 46px;
    font-size: 14px;
    position: relative;
    width: 100%;
    border-bottom: 1px solid #e5e5e5;
    background: #C8E4C8 !important;
}

.header .nav .nav-parent {
    height: 46px;
    background: #C8E4C8 !important;
    padding: 0 10px;
    border-radius: 3px 3px 0 0;
    width: 1190px;
}

.block-video .block-title p {
    -webkit-transition: bottom .2s ease,opacity .2s ease;
    -moz-transition: bottom .2s ease,opacity .2s ease;
    -ms-transition: bottom .2s ease,opacity .2s ease;
    -o-transition: bottom .2s ease,opacity .2s ease;
    transition: bottom .2s ease,opacity .2s ease;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    background: rgba(255,255,255,0.7) !important;
}

.main {
    background: #C8E4C8 !important;
}

#channel-nav {
    background: #C8E4C8 !important;
}

.footer {
    padding: 20px 0;
    background-color: #C6e0C0  !important;
    font-size: 12px;
}


.introduction {
    background: #b2d3b2 !important;
    border-top: 1px solid #b2d9b9 !important;
}

.comment-area {
    padding-bottom: 50px;
    background: #b2d3b2 !important;
}

.block {
    background-color: rgba(255,255,255,0.7) !important;
    padding: 10px !important;

}










}
