css:
@-moz-document domain("weebly.com") {
div.wsite-section.wsite-header-section.wsite-section-bg-image{
    background-image: url(http://wallpaperswide.com/download/ilse_waterfall_harz_national_park_saxony_anhalt_germany-wallpaper-1920x1080.jpg)!important;
    
}
.topbar{
    width: 5%;
    height: 40px;
}
.logo{
    display: none;
}
.main-wrap{
    background: #272727;
}
h2{
    color: #dcdcdc;
}
a {
    color: #878787;
}
p.blog-share{
    display: none;
}
#commentPostDiv .field input[type=text], #commentPostDiv .field textarea {
    background: #969696;
}
#commentPostDiv {
    color: white!important;
}
#blogTable .blog-post .blog-date {
    color: #ffffff;
}
#blogTable .blog-post h2.blog-title a {
    color: #a5a0a0;
}
.nav-wrap {
    top: 0;
    left: 5%;
}
body.w-navpane-is-forced .nav-wrap {
    text-align: center;
}
div.w-navlist nav-wrap{
    background-color: rgba(0,0,0,0.5);
}
body.w-navpane-is-forced.nav-open .header-wrap label.hamburger {
    background: rgba(0,0,0,0.5);
}
}
