.ktcYN .Pe5z7e .CG2qQ.TE4AFe:before{
    content: "CLICK >> ";
    font-size: 40px;
    position: relative;
	top: -6px;
    animation: rainbow 3s infinite; 
}

@keyframes rainbow{
