css:
@-moz-document domain(trello.com) {
    /* __FEATURES__ */
    
    body .list-card-labels {
        float: right;
        top: -2px;
        right: -6px;
        max-width: 200px;
        margin: 0;
        text-align: center;
    }
    .body-card-label-text-on .card-label.mod-card-front,
    .body-card-label-text .card-label.mod-card-front {
        min-width: auto;
        text-shadow: 0 1px 1px rgba(0, 0, 0, .6);
    }
    /* display card id */
    body .card-short-id.hide {
        position: absolute;
        top: 0;
        left: 0;
        display: block;
        height: 16px;
        line-height: 16px;
        padding: 0 3px;
        font-weight: bold;
        font-size: 11px;
        border-radius: 3px 0;
    }
    body .has-cover .card-short-id.hide {
        border-radius: 0 0 3px 0;
    }
    body .list-card-title {
        margin: 16px 0 10px;
    }
    /* bottom edit link */
    body .list-card-operation {
        top: auto;
        right: -2px;
        bottom: -2px;
        padding: 1px;
        border-radius: 3px;
        box-shadow: 1px 1px rgba(0, 0, 0, .3), -1px -1px white;
    }
    body .list-card {
        margin-top: 10px;
    }
    body .list-card:first-child {
        margin-top: 2px;
    }
    /* display total cards count */
    body .list-header-num-cards.hide {
        display: inline-block;
        font-size: 11px;
    }
	
    /* __STYLE__ */
	
	/* background */
	/* background - wallpaper */
	#classic-body {
        background-image: url(http://nsm09.casimages.com/img/2018/03/07//18030710291411028715601231.png) !important;
        background-size: cover !important;
        background-position-x: center !important;
    }
	/* background - scrollbar */
    .u-fancy-scrollbar::-webkit-scrollbar-track-piece {
        background: none;
    }
    #board.u-fancy-scrollbar::-webkit-scrollbar-thumb:horizontal,
	.u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical {
        background: #606060;
    }
	/* background - label */
	.card-label-blue {
        background-color: #005282;
    }
    .card-label-green {
        background-color: #388628;
    }
    .card-label-lime {
        background-color: #00966d;
    }
    .card-label-orange {
        background-color: #c17824;
    }
    .card-label-pink {
        background-color: #bd468f;
    }
    .card-label-purple {
        background-color: #78149e;
    }
	.card-label-red {
        background-color: #9c1f0e;
    }
    .card-label-sky {
        background-color: #009bb3;
    }
    .card-label-yellow {
        background-color: #ad9900;
    }
	/* background - general */
    code,
    pre,
	.list-header,
    .list-header > *,
	.member,
	.member:hover {
        background: none !important;
    }
	form span:hover,
    input[type=button].primary:hover,
    input[type=submit].primary:hover,
    #header,
	.list-card.active-card,
    .list-wrapper.mod-add.is-idle,
    .body-board-view #header,
	.open-card-composer:hover {
        background: #000 !important;
    }
    .list-card.is-drophover .list-card-dropzone {
        background: rgba(0, 0, 0, 0.75);
    }
    .window-wrapper .is-drophover .dropzone {
        background: rgba(0, 0, 0, 0.58);
    }
    .board-header {
        background: #000;
        background: -webkit-linear-gradient(top, #000 50%, rgba(0, 0, 0, 0));
        background: -moz-linear-gradient(top, #000 50%, rgba(0, 0, 0, 0));
        background: -o-linear-gradient(top, #000 50%, rgba(0, 0, 0, 0));        
        background: linear-gradient(to bottom, #000 50%, rgba(0, 0, 0, 0));
    }
    textarea:focus,
    .checklist-item-checkbox,
	.checklist-progress-bar,
    .compact-board-tile-background,
    .compact-board-tile-fade,
    .list-card-placeholder,
    .list-card.placeholder,
    .pop-over,
	.pop-over.mod-notifications,
    .board-menu-navigation-item-link:not(.disabled):hover,
    .button-link:hover,
    .card-detail-badge.is-clickable:hover,
	.card-detail-item-add-button:hover,
    .show-more:hover {
        background-color: #151515;
    }
	body,
    input[type=button].primary,
    input[type=submit].primary,
    input:not([type=file]),
    textarea,
    textarea.mod-card-back-title.is-editing,
    textarea.mod-card-back-title:focus,
    .boards-drawer-header,
    .button-link,
    .action-comment,
    .card-detail-badge,
    .card-detail-item-add-button,
    .checklist-new-item.focus .checklist-new-item-text,
    .comment-box-input,
    .comment-box,
    .helper,
    .list-card,
	.open-card-composer,
    .tabbed-pane-header,
    .tabbed-pane-main-col,
    .tabbed-pane-nav,
    .tabbed-pane-nav-item-button.active,
    .attachment-thumbnail:hover .attachment-thumbnail-details,
    .board-menu-navigation-item-link:hover,
    .board-menu-section-header:hover,
	.checklist-item:hover,
    .checklist-new-item-text:hover,
    .checklist-new-item.focus .checklist-new-item-text:focus,
    .checklist-new-item.focus .checklist-new-item-text:hover,
    .pika-single .pika-next:hover,
    .pika-single .pika-prev:hover,
    .pika-single .pika-button:hover,
    .pika-single .pika-label:hover,
    .quiet-button:hover,
    .tabbed-pane-nav-item-button:hover,
    .tabbed-pane-nav-item-button.active:hover {
        background-color: #202020 !important;
    }
    button,
    select,
    thead,
    input[type=text]:focus,
    input[type=password]:focus,
    input[type=email]:focus,
    .boards-drawer,
    .board-menu,
    .board-menu-tab-content,
    .iframe-input,
    .iframe-input.focus,
    .list-card-edit-title,
    .org-bc-settings,
    .pika-single,
    .pika-single .pika-button,
    .pika-single .pika-table td,
    .window,
    .list-card-edit-title:focus {
        background: #303030 !important;
    }
    a.button-link.tabbed-pane-header-details-edit.js-edit-profile,
	form,
    .compact-board-tile.selected .compact-board-tile-fade,
    .list,
    .list-wrapper.mod-add,
    .tabbed-pane-nav-item-button,
    .checklist-item-checkbox.enabled:hover {
        background-color: #404040 !important;
    }
    .window-archive-banner {
        background-color: #555555;
    }
    .card-short-id {
        background: #fff !important;
    }
    .action-comment-fade-button {
        background: linear-gradient(to bottom, rgba(255, 255, 255, 0), #151515);
    }
    .pop-over-list li > a:hover {
        background-color: #005282;
    }
    button.primary,
    input[type=button].primary,
    input[type=submit].primary,
    .button.primary	{
        background-color: #388628;
    }
    .badge.is-due-past,
    .card-detail-badge.is-due-past,
    .card-detail-badge.is-due-past:hover {
        background-color: #6d0d00;
    }
    button.negate,
    input[type=button].negate,
    input[type=submit].negate,
    .app-alert-item.mod-error,
    .badge.is-due-now,
    .button.negate,
    .button-link.negate,
    .card-detail-badge.is-due-now,
    .card-detail-badge.is-due-now:hover {
        background-color: #9c1f0e;
    }
	
	
	
	/* filter */
    .member-avatar:hover,
    .member-initials:hover {
        filter: brightness(50%);
    }
    .card-detail-badge.is-due-now:hover,
    .card-detail-badge.is-due-past:hover {
        filter: brightness(130%);
    }
	
	
	
	/* box-shadow */
	form,
    input[type=button].primary,
    input[type=submit].primary,
    .button-link,
	.list-header,
	.open-card-composer,
    .button-link:hover {
        box-shadow: none !important;
		-webkit-box-shadow: none !important;
    }
    button.primary,
    input[type=button].primary,
    input[type=submit].primary,
	.button.primary {
        box-shadow: 0 1px 0 #388628;
    }
	
	
	
	/* opacity */
    .compact-board-tile-background,
    .compact-board-tile-fade {
        opacity: 0.1;
    }
	.list-card-operation {
        opacity: 0.5;
    }
    .compact-board-tile-background,
    .compact-board-tile-fade {
        opacity: 1;
    }
	
	
	
	/* border */
    code,
    pre,
    .app-alert-item.mod-error {
        border: none;
    }
    textarea:focus,
	.checklist-progress-bar,
    .checklist-item-checkbox,
    .list-card.active-card,
    .pop-over.mod-notifications,
    .pop-over {
        border-color: #151515;
    }
    textarea.mod-card-back-title.is-editing,
    textarea.mod-card-back-title:focus,
    .checklist-new-item.focus .checklist-new-item-text,
    .button-link,
    .card-detail-badge,
    .helper,
    .action-comment,
    .card-detail-item-add-button,
    .comment-box-input,
    .comment-box,
    .list-card,
    .checklist-item:hover,
    .checklist-new-item-text:hover,
    .checklist-new-item.focus .checklist-new-item-text:focus,
    .checklist-new-item.focus .checklist-new-item-text:hover,
    .quiet-button:hover {
        border-color: #202020;
    }
    button,
    input:not([type=file]),
    textarea,
    .iframe-input {
        border-color: #303030;
    }
    .checklist-item-checkbox.enabled:hover {
        border-color: #404040;
    }
    .card-short-id {
        border-color: #fff !important;
    }
	
    .member-initials {
        border-radius: 25em;
    }
	
	
	/* color */
    .card-short-id {
        color: #000 !important;
    }
    a,
    body,
    button,
    button.primary,
	form span,
    html,
    input,
    input[type=button].primary,
    input[type=submit].primary,
    select,
    textarea,
	.button.primary,
    .checklist-new-item.focus .checklist-new-item-text,
    .compact-board-tile-link-text-sub-name,
	.open-card-composer,
    .dialog-close-button,
    .list-header-num-cards,
	.list-card-title,
    .pop-over-header-title,
	.tabbed-pane-nav-item-button,
    .tabbed-pane-nav-item-button.active,
    .board-menu-navigation-item-link:hover,
    .board-menu-section-header:hover,
    .button-link:hover,
    .checklist-new-item.focus .checklist-new-item-text:focus,
    .checklist-new-item.focus .checklist-new-item-text:hover,
    .open-card-composer:hover,
    .pika-single .pika-next:hover,
    .pika-single .pika-prev:hover,
    .pika-single .pika-button:hover,
    .pika-single .pika-label:hover,
    .show-more:hover,
    .tabbed-pane-nav-item-button:hover,
    .tabbed-pane-nav-item-button.active:hover {
        color: #dedede !important;
    }
    .cpoints,
    .points {
        color: #a5a5ff;
    }
}
