css:
@namespace url(http://www.w3.org/1999/xhtml);
/* https://fonts.googleapis.com/css?family=Open+Sans' */
@-moz-document domain('slashdot.org') {
/* CENTERING */

body {
    width: 90% !important;
    min-width: 900px;
    margin: 0 auto !important;
}
/* Set width of content and center it */

header.h {
    width: auto !important;
}
/* Make header fit new width */
/* REMOVE SIDEBARS */

.col_1,
#slashboxes {
    display: none !important;
}
#firehose {
    margin: 0 !important;
}
/* Remove space reserved for sidebars on the frontpage */

section#comments {
    margin-left: 0 !important;
    padding: 0 20px !important;
}
/* Remove space reserver for left sidebar on comments page */

.main-wrap.has-rail-right > .main-content {
    margin-right: 0 !important;
}
/* SLIM DOWN ARTICLE HEADER */

.details br {
    display: none !important;
}
/* Remove the new line for the 'From the ... dept' text */
/* COMMENT LINK ON FRONTPAGE ARTICLES */

.comments {
    display: inline !important;
    font-size: 100% !important;
}
/* Displays 'Read the XXX comments' instead of 'Read the comments' */

.article .body .p {
    margin: 5px 20px !important;
}
/* Less whitespace around the 'Read the comments' link */
/* MOVE LOGIN BAR */

.nav-user {
    position: fixed !important;
    right: 0 !important;
}
/* REMOVAL OF SEVERAL ELEMENTS */

header span.comments,
/* Comment count in article header */

.daybreak,
/* The break of today's and yesterday's news */

.tags,
/* Tags below articles */

article aside,
/* On comment page: Related stories, Share this story, Similar stories with tags, etc */

#commentwrap > p
/* The Fine Print (Legal Mumbo Jumbo) */

{
    display: none !important;
}
/* BACKGROUND IMAGE */

html,
body,
header.h {
    background: url("data:image/gif;base64,R0lGODlhKAAoAIAAACgoKCAgICwAAAAAKAAoAAACagQShprX65iMEdqH59VpUw9mH0d+IliG6dmx61u5JozOtnrTMr7w+38DBjW64miI9CWFKuUyZXRaosyMtCq5UhXb56Gr1Xp72PEDzEQXxeEyW52EK9lYelS+w7/sT75Un2O25QdESAMIUgAAOw==") !important;
}
header.h {
    background-position: 6px 0px !important;
}
/* Position header background to make it fit the HTML background */

body {
    background-position: 3px 0px !important;
}
/* Position body background to make it fit the HTML background */
/* VISUAL FIXES */

#modal_box {
    margin: 0 !important;
}
/* Display login and option box in a more sensible location */

.nav-search-form {
    margin-left: -102px
}
.nav-wrap {
    border-radius: 8px 8px 0 0;
}
#fhft,
footer#ft {
    min-width: 0;
}
#comments.container {
    min-width: 854px;
}
.grid_24 {
    min-width: 275px;
    margin-left: -1px;
}

#clickgen {
    margin-top: -15px;
    width: auto;
    min-width: 841px;
}

.grid_16.f {
    display: inline-block;
    float: none !important;
    min-width: 400px;
}

.grid_8.st {
    margin-bottom: 14px;
}

.grid_12 {
    font-size: 90%;
}

#fhft {
    margin-top: -40px;
    border-radius: 0 0 8px 8px;
}

#fhft.grid_24.nf {
    margin-top: 0;
}

#fhft.grid_24.nf.aoc {
    margin-top: -40px;
}

.nav-user {
    position: relative;
    z-index: 9999;
}

.container, h2, .story-title, header h2, h2.story, .p {
    font-family: "Open Sans", "Noto Sans", sans-serif;
}

h2, .story-title, header h2, h2.story {
    font-size: 11pt;
    padding-left: 0 !important;
}

h2.story {
    border-radius: 4px 4px 0 0;
    padding: 8px 110px 8px 0 !important;
    margin-left: 0;
    line-height: 100%;
    text-shadow: 0 1px 1px rgba(0,0,0,0.6);
}

.story-title a {
    line-height: 105% !important;
}

.details {
    border-radius: 0 0 4px 4px;
}

.p {
    text-align: justify;
}

#firehose article:not(.briefarticle) {
    margin-bottom: 0;
}

#firehose article header .details {
    padding-left: 10px;
}

#d2header > div > aside {
    opacity: 0;
    margin: -8px 0;
}

#firehose-message-tray.message-bar {
    margin-bottom: 19px;
}

#d2header > div > nav.grid_24, #clickgen > section > div, #clickgen > aside > div {
    border-radius: 4px 4px 0 0;
}

section#besttabs {
    border-radius: 0 0 4px 4px;
}

.cmntcnt > span:not(.slant) {
    width: 30px;
    margin-right: 10px;
}

.btn:focus, .btn:hover {
    border: 1px solid #fff;
}

.tops {
    margin-top: 2px;
    margin-right: -12px;
}

section#besttabs {
    padding-top: 0;
    margin-top: -3px;
}

.cmntcnt_large {
    font-size: 12pt !important;
    margin-right: 5px;
    float: right !important;
    font-weight: bold;
}

div#comments.a2commentwrap {
    margin-right: -8px;
}

.banner-wrapper, #announcement, .banner-contain {
    display: none !important;
}

.nav-search-form {
    margin-left: 0;
    margin-right: 45%;
}

.nav-search-form input[type="text"] {
    border-radius: 2px 6px 2px 2px;
}

.article .body .p {
    border-radius: 0 0 4px 4px;
    background: linear-gradient(to top, #eee, #fff 5px);
    margin: -2px 0 !important;
    border: 1px solid #f2f2f2;
    padding: 10px;
}

.comment.full.contain {
    border-radius: 8px 0 4px 4px !important;
    text-align: justify;
}

.nav-user {
    margin-right: -6%;
    margin-top: -1px;
    position: relative !important;
    height: 66px;
    overflow: hidden !important;
    right: -6% !important;
}

.nav-primary .btn-success {
    margin-top: -5px;
}

#ft, .adwrap, .nav-social, div[id*="-ad-"], #bottomadspace, div[id*="pop"], .janrainProviderList, .deals-wrapper, .story-controls {
    display: none !important;
}

.user-access {
    width: auto;
    float: none;
    position: relative;
    top: -15px;
    line-height: 1;
    padding: 5px 8px !important;
    border-radius: 5px;
    background: #004d4d;
}

h2.firehose {
    border-radius: 8px 8px 0 0;
}

h2.firehose + .details {
    border-radius: 0 0 8px 8px;
}

.firehose .story-ribbon {
    border-radius: 7px 0 0 0;
    margin: 0 10px 0 5px;
    padding-bottom: 1px;
}

.clearfix.meta.article-foot {
    margin: -15px 0 32px !important;
}

.container {
    overflow-x: hidden;
}

.has-rail-right {
    overflow: hidden;
}

.article .body .p > i {
    font-style: italic;
}

.p i + br:last-of-type {
    display: none;
}

#firehose article:not(.briefarticle), .article h2 {
    filter: drop-shadow(0 1px 1px #999);
}

.article h2:first-of-type {
    margin-top: 1px !important;
}

.article .body .p {
    border-top: 1px solid #ddd;
}

header img {
    position: relative;
    z-index: 999;
}

header img {
    position: relative;
    z-index: 999;
    border: 1px solid #016765;
    border-radius: 50%;
    padding: 6px;
    background: #ddd;
    background: linear-gradient(to bottom, #f2f2ff, #ddd);
    box-shadow: inset 0 0 0 1px #fff;
    width: 36px;
    height: 36px;
}

.ccw-score-label {
    background: #999;
    border-radius: 4px;
    box-shadow: 0 0 0 1px #999;
}

.ccw-score-label .sc {
    margin-left: 0 !important;
}

#d2header .ccw-score-label .sc, #d2header .ccw-score-label > div {
    font-weight: bold;
    font-style: italic;
    background: #999;
    opacity: 0.85;
    border-top: 1px solid #aaa;
}

.inline_comment {
    border: 1px solid #bbb;
    border-radius: 4px;
    margin: -10px 0 30px !important;
    background: #eee;
}

#clickgen .tlnav {
    background: #999;
    font-weight: bold;
}

.paginate {
    text-align: center;
    margin: auto;
}

a.prevnextbutact, a.prevnextbutdis {
    border: none;
    padding: 3px 0;
    box-shadow: 0 0 0 1px #ddd inset;
}

a.prevnextbutdis {
    cursor: default;
}

.slant {
    top: -1px;
}

blockquote.msg.grid_24 {
    width: auto;
}

blockquote.msg > p {
    margin-bottom: -14px;
}
}
