css:
@-moz-document domain("sonyalpharumors.com") {
body.owt,
body.owt .navigation .page-numbers:hover
{
	background-color: #202020;
}

body.owt #container #content .post,
body.owt #header .nav ul li.searchformbox .searchform,
body.owt #header .nav ul li.searchformbox .searchform .searchsubmit,
body.owt .navigation,
body.owt #container #sidebar .addpadder .widget,
body.owt .navigation .page-numbers,
body.owt #header .nav ul li a:hover
{
	background-color: #191919;
}

body.owt,
body.owt #container #content .post,
body.owt blockquote,
body.owt h3 a,
body.owt #header .nav ul li a,
body.owt #header .nav ul li.searchformbox .searchform,
body.owt #header .nav ul li.searchformbox .searchform .searchsubmit,
body.owt #container #sidebar .addpadder .widget,
body.owt h1,
body.owt h2,
body.owt h3,
body.owt h4,
body.owt h5,
body.owt h6,
body.owt .navigation .page-numbers
{
	color: #909090;
}

body.owt a:hover
{
	color: #777;
}

body.owt .navigation .page-numbers.current
{
	background: #FF6F00;
}

body.owt .boxinfo,
body.owt .navigation .page-numbers.current,
body.owt #sidebar .widgettitle
{
	color: #000;
	font-size: 1.2em;
}

#logo a.logo-inner
{
	background-size: 106% 106%;
	filter: brightness(42%);
}

/**Disable Ad, Sidebar and Footer**/
body.owt #skyscraper-ad.fixed,
body.owt #skyscraper-ad,
body.owt .promobox,
.onesignal-reset,
.addtoany_content,
body.owt #container #sidebar,
body.owt #footer,
*[id*='_adr_abp']
{
	display: none !important;
	pointer-events: none;
}

/**Use Full Width**/
body.owt #container #content
{
	width: 100%;
}

/**Center Images**/
body.owt #container #content .post .postmain .post-content img
{
	display: block;
	margin-left: auto;
	margin-right: auto;
}
}
