css:
@-moz-document domain("readthedocs.io")
{
a,
a:visited,
a:hover {
    color: #f1362f;
}
p,
h1,
h2,
h3 {
    font-family: "Lato", "proxima-nova", "Helvetica Neue", Arial, sans-serif !important;
}
.wy-nav-side a:hover {
    color: #f4645f;
    background-color: #222 !important;
}
.toctree-l1 > a {
    color: white !important;
}
.wy-nav-side {
    border-right: 1px solid #f0f2f1;
}
.wy-nav-side * {
    background-color: #222 !important;
}
.toctree-l2 a {
    color: #f4645f !important;
}
.toctree-l2 a:hover {
    color: #e74430 !important;
}
.wy-menu-vertical li.current > a {
    border: none;
}
pre {
    line-height: 24px;
    background-color: #263238 !important;
    color: #C3D3DE !important;
    font-family: "Operator Mono", "Fira Code", Consolas, Monaco, "Andale Mono", monospace !important;
    border-radius: 3px;
}
.highlight-php,
.highlight-default,
.highlight {
    border: none !important;
    border-radius: 3px;
    background: #222 !important;
}
.wy-nav-content {
    background: #222222;
    color: aliceblue;
    max-width: none;
    padding-right: 300px;
}
.wy-nav-content a {
    text-decoration: underline;
    color: #f4645f;
}
.fa-github {
    color: white !important;
    text-decoration: none !important;
}
.wy-side-nav-search input[type=text] {
    border: 1px solid #f0f2f1;
    border-radius: 0px;
    box-shadow: none;
}
.note > .first {
    background-color: #3683C0 !important;
}
.warning > .first {
    background-color: #f4645f !important;
}
.important > .first {
    background-color: #493079 !important;
}
body,
html {
    background-color: #222222;
    background: #222222;
}
.wy-nav-content-wrap {
    background-color: rgba(0, 0, 0, 0.0) !important
}
.method dt,
.class dt,
.data dt,
.attribute dt,
.function dt,
.descclassname,
.descname {
    background-color: #525252 !important;
    color: white !important;
}
.toc-backref {
    color: grey !important;
}
code.literal {
    background-color: #263238 !important;
    border: none !important;
    border-radius: 3px;
    color: #C3D3DE !important;
}
.wy-nav-content-wrap {
    background-color: rgba(0, 0, 0, 0.6) !important;
}
.sidebar {
    background-color: #191919 !important;
}
.sidebar-title {
    background-color: #2b2b2b !important;
}
.xref,
.py-meth {
    color: #7ec3e6 !important;
}
.admonition,
.note {
    background-color: #2d2d2d !important;
}
.s1 {
    color: #c3e88d;
}
.o,
.p {
    color: #89ddff;
}
.c1 {
    color: #546E7A;
}
.nx {
    color: #FFCB6B;
}
.na {
    color: #82AAFF;
}
.k {
    color: #BF8DE1;
    font-weight: normal;
}
.n,
.s2 {
    color: white;
}
.nv {
    color: #EEFFE3;
}
.nf {
    color: #C792EA;
    font-weight: normal !important;
}
.mi {
    color: #FFCB6B;
}
}
