css:
@-moz-document url-prefix("http://127.0.0.1:8000/admin/"), url-prefix("http://localhost:8000/admin/") {
 /*start style*/
body {
    margin: 0;
    padding: 0;
    font-size: 14px;
    font-family: Ubuntu;
    color: #999;
    background: #162230;
}

body.login {
    background: #142029;
}

.login #container {
    background: #162230;
}

.login .form-row label {
    color: #f03db5;
}

tr {
	background: #0c121a;
}

#content-related {
    background: #000101;
}

#changelist .paginator {
    color: #f5dd5d;
    border-bottom: 1px solid #eee;
    background: #0f1b2a;
    overflow: hidden;
}

.module {
    border: none;
    margin-bottom: 30px;
    background: #0c121a;
}

.submit-row {
    padding: 12px 14px;
    margin: 0 0 20px;
    background: #0c121a;
    border: 1px solid #eee;
    border-radius: 4px;
    text-align: right;
    overflow: hidden;
}

#changelist #toolbar {
    padding: 8px 10px;
    margin-bottom: 15px;
    border-top: 1px solid #eee;
    border-bottom: 1px solid #eee;
    background: #7e9da5;
    color: #666;
}

#changelist-filter {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 1000;
    width: 240px;
    background: #0c121a;
    border-left: none;
    margin: 0;
}

#changelist-filter li.selected {
    border-left: 5px solid #fefc61;
    padding-left: 10px;
    margin-left: -15px;
}

#changelist-filter li.selected a {
    color: #f1e78c;
}

#changelist-filter h3 {
    font-weight: 400;
    font-size: 1.15em;
    padding: 0 15px;
    margin-bottom: 10px;
    color: #924d8b;
}

fieldset.collapsed h2 {
    background: #140101;
    color: #b8ddf0;
    font-size: 1.1em;
}

fieldset.collapsed {
    border: 1px solid #4c0808;
    
}

input[type=text], input[type=password], input[type=email], input[type=url], input[type=number], textarea, select, .vTextField {
    border: 4px solid #447e9b;
    border-radius: 4px;
    padding: 5px 6px;
    margin-top: 0;
    background: #0b181a;
    color: #f5deb3;
    font-size: 1.35em;
}

select {
    height: 2.7em;
}

.inline-group div.add-row, .inline-group .tabular tr.add-row td {
    color: #666;
    background: #0c121a;
    padding: 8px 10px;
    border-bottom: 1px solid #eee;
}

table thead th .text a {
    display: block;
    cursor: pointer;
    padding: 8px 10px;
    color: #a300ff;
    font-size: 1.5em;
}

table thead th .text span {
    padding: 8px 10px;
    display: block;
    color: #000eff;
    font-size: 1.5em;
}

.nowrap {
    white-space: nowrap;
    color: #999;
}





td, th {
    font-size: 1.25em;
    line-height: 16px;
    border-bottom: 1px solid #eee;
    vertical-align: top;
    padding: 8px;
    font-family: Ubuntu;
    background: #0c121a;
}

thead th {
    padding: 5px 10px;
    line-height: normal;
    text-transform: uppercase;
    background: #84b0cd;
}

.inline-related fieldset {
    margin: 0;
    background: #0c121a;
    border: none;
    width: 100%;
}

#changelist #toolbar form #searchbar {
    height: 19px;
    border: 1px solid #f550e8;
    padding: 2px 5px;
    margin: 0;
    vertical-align: top;
    font-size: 1.35em;
    color: #f550e8;
    min-height: 1.5em;
}

#changelist #toolbar form input[type="submit"] {
    border: 2px solid #ccc;
    padding: 2px 10px;
    margin: 0;
    vertical-align: middle;
    background: #8b4485;
    box-shadow: 0 -15px 20px -10px rgba(0, 0, 0, 0.15) inset;
    cursor: pointer;
    color: #0d083d;
}

#changelist #toolbar form input[type="submit"]:hover {
    border: 2px solid #ccc;
    padding: 2px 10px;
    margin: 0;
    vertical-align: middle;
    background: #8b4485;
    box-shadow: 0 -15px 20px -10px rgba(0, 0, 0, 0.15) inset;
    cursor: pointer;
    color: #0d083d;
}

#changelist .actions {
    padding: 10px;
    background: #213146;
    border-top: none;
    border-bottom: none;
    line-height: 24px;
    color: #999;
}

thead th.sorted {
    background: #5f9dc6;
}

.selector .selector-available h2 {
    background: #0b181a;
    color: #f6bb9c;
    border: 1px solid #424141;
}

.selector .selector-filter {
    background: #162230;
    border: 1px solid #424141;
    border-width: 0 1px;
    padding: 8px;
    color: #999;
    font-size: 10px;
    margin: 0;
    text-align: left;
}

.selector-chosen h2 {
    background: #162230;
    border: 1px solid #424141;
    color: #f6bb9c;
}

/* FORM */

label {
    font-weight: normal;
    color: #d79ee8;
    font-size: 1.3em;
}

p.url {
    line-height: 25px;
    margin: 0;
    padding: 0;
    color: #d384f1;
    font-size: 1em;
    font-weight: bold;
}

.required label, label.required {
    font-weight: bold;
    color: #e36e6e;
    font-size: 1.2em;
}

a:link, a:visited {
    color: #6dbae1;
    text-decoration: none;
    font-size: 1.3em;
}

.inline-group .tabular td.original p {
    font-size: 1em;
    font-weight: bold;
    color: #6aa4e6;
}
    
     /*end style*/
}
