css:
@-moz-document url-prefix("https://pirates-forum.org") {
table,
body,
textarea {
    color: #d3d3d3 !important;
}
.tborder,
#footer,
body {
    background: #1d1d1d !important;
}
.post.classic .post_author {
    border: 1px solid #1d1d1d !important;
}
.post .post_author,
blockquote,
.post_controls,
#panel .lower,
#footer .upper.tpb_links,
#footer .upper.sb_links,
#footer .upper.contact,
textarea, .codeblock {
    background: #2f2f2f !important;
}
.post .post_author div.author_avatar img {
    padding: 0px !important;
    margin: 5px !important;
    box-shadow: 0 0 12px rgb(255, 255, 255) !important;
    background: none !important;
}
.reputation_positive {
    color: #06b506 !important;
}
.post_body a:link,
.post_body a:visited {
    color: #03A9F4 !important;
}
#content {
    background: #1d1d1d !important;
    color: #d3d3d3 !important;
    box-shadow: 0 0 8px rgb(255, 255, 255) !important;
}
a:link,
a:visited,
a:hover,
a:active {
    color: #d3d3d3 !important;
}
.navigation .active,
.pagination .pagination_current,
blockquote cite > span,
#container {
    color: #fff !important;
}
.pagination a {
    background: #3e3e3e !important;
}
.post .post_author div.author_statistics {
    color: #c7c7c7 !important;
}
.postbit_buttons > a:link {
    background: none !important;
}
.tfoot {
    border-top: 1px solid #5d5d5d !important;
    background: #1d1d1d !important;
}
.trow1,
.trow2 {
    background: #2f2f2f !important;
    border-color: #7b7b7b !important;
}
#site_status,
#team_no_pm {
    background: #272727 !important;
}
*::-webkit-scrollbar {
    width: 10px !important;
    height: 10px !important;
    background: #1e1e1e !important;
    border: 1px solid #252525 !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: #333 !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1e1e1e !important;
}
}
