css:
@-moz-document regexp('https://support\\.mozilla\\.org/.+/kb/.+/[0-9]+') {
    .review #main-content > div {
    	width: unset;
	}
    .review #main-container {
        width: auto;
    }
    .review #main-container > aside {
        float: none;
    }
    .review #review-revision > details:nth-child(8) {
        display: inline-block;
        width: 49%;
    }
    .review #review-revision > details:nth-child(9) {
        display: inline-block;
        width: 49%;
    }
}
