css:
@-moz-document domain("amazon.com"), domain("amazon.in"), domain("amazon.co.jp"), domain("amazon.com.tr"), domain("amazon.sg"), domain("amazon.fr"), domain("amazon.de"), domain("amazon.it"), domain("amazon.nl"), domain("amazon.es"), domain("amazon.co.uk"), domain("amazon.ch"), domain("amazon.ca"), domain("amazon.com.mx"), domain("amazon.com.au"), domain("amazon.com.br") {
/* Outline hover - burger menu layout */
#navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover.nav-packard-glow #nav-global-location-slot .nav-a:hover,
#navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover #nav-logo .nav-logo-link:hover,
#navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover #nav-pldn-msg-wrapper .nav-pldn-a:hover, #navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover #nav-shop #nav-link-fresh:hover, #navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover #nav-shop #nav-link-shopall:hover, #navbar[data-template=layoutSwapToolBar].layout2.bold-focus-hover .nav-right #nav-tools .nav-a:hover {
  outline: none;
  border-color: transparent;
}

/* Amazon Prime */
#nav-logo .nav-prime-try {
    display: none;
}

#nav-tools #nav-link-prime {
  display: none;
}

#navSwmHoliday {
  display: none;
}

/* Link list */
#nav-xshop-container {
    display: none;
}

/* Logo - without Prime */
#navbar[data-template=layoutSwapToolBar].layout2 #nav-logo .nav-logo-link .nav-logo-base, 
#navbar[data-template=layoutSwapToolBar].layout2 #nav-logo .nav-logo-link .nav-logo-ext,
#navbar[data-template=layoutSwapToolBar].layout2 #nav-logo .nav-logo-link .nav-logo-locale {
  margin-top: 16px;
}

#navbar[data-template=layoutSwapToolBar].layout2 #nav-logo .nav-logo-link .nav-logo-locale {
  margin-top: 21px;
}

/* Hide name */ 
#nav-tools #nav-link-accountList .nav-line-1 {
  display: none;
}

#nav-tools #nav-link-accountList .nav-line-2 {
  padding-top: 22px
}

/* Orders */
#nav-tools #nav-orders {
  display: none;
}

/* Search bar */
#nav-search {
  margin-right: 40px !important;
}

/* Layout */
#nav-belt {
  padding-bottom: 0;
  display: flex;
  justify-content: space-between;
}

#nav-belt > .nav-right {
  order: 3;
  margin-right: 40px;
}

#nav-belt > .nav-fill {
  flex: 1;
}

#navbar[data-template=layoutSwapToolBar].layout2 #nav-belt > .nav-fill, 
#navbar[data-template=layoutSwapToolBar].layout2 #nav-belt > .nav-left, 
#navbar[data-template=layoutSwapToolBar].layout2 #nav-belt > .nav-right {
  height: 55px !important;  
}

/* Layout basket */
#nav-tools .nav-a-2 .nav-line-2 {
  line-height: 10px
}

#nav-tools #nav-cart .nav-cart-icon {
  top: 9px;
}

#nav-tools #nav-cart .nav-cart-count {
  top: 5px !important;
}

/* Layout address */
#nav-main,
#navbar.nav-bluebeacon #nav-belt,
#navbar.nav-bluebeacon #nav-main {
  background-color: transparent;
}

#nav-main {
  width: auto;
  height: 28px !important;
  margin-left: 204px;
}

#nav-main .nav-left{
  height: 100% !important;
}


#nav-global-location-slot {
  width: 100% !important;
}

#nav-global-location-slot .nav-a {
  line-height: 22px !important;
  height: 22px !important;
}

#nav-global-location-slot #nav-packard-glow-loc-icon {
  top: 0 !important;
}

#nav-global-location-slot #glow-ingress-block {
  display: flex;
  margin-top: 2px;
}

#nav-global-location-slot #glow-ingress-block #glow-ingress-line1 {
  margin-top: 1px;
  padding-right:7px
}
}
