css:
@-moz-document url-prefix("https://lmaobox.net/") {
/*Basic Background Color*/
body {
    background-color: #1b1b1b;
}
/*Text Color*/
body {
    margin: 0;
    font-family: Arial, Helvetica, Verdana, sans-serif!important;
    font-size: 14px;
    line-height: 20px;
    color: #676666;
}

a:hover, a:focus {
    color: #6d0aa9;
    text-decoration: underline;
}
a:hover, a:active {
    outline: 0;
}
a {
    color: #8605e4;
    text-decoration: none;
}


/*NAV Bar Editting*/
.navbar-inverse .navbar-inner {
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#55109e), to(#222222));
}
.navbar-inverse .nav .active>a, .navbar-inverse .nav .active>a:hover, .navbar-inverse .nav .active>a:focus {
    color: #ffffff;
    background-color: #421971;
}
.navbar-inverse .brand, .navbar-inverse .nav>li>a {
    color: #989898;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
}

/*The Forums*/
#Frame, body {
    background-color: #1b1b1b;
    text-align: left;
}
/*Forum unread*/
.DataList .ItemDiscussion {
    background: #200033;
}
/*Forum read*/
.Item.Read {
    background: #3c1b50;
    opacity: 1;
}

/*TextBox*/
input.DateBox, input.InputBox, input.SmallInput, textarea {
    font-family: lucida grande,lucida sans unicode,tahoma,sans-serif;
    color: #6400ff;
    font-size: 15px;
    padding: 3px;
    margin: 0;
    width: 250px;
    background: #000;
    border: 1px solid #999;
    border: 1px solid rgb(0, 0, 0);
}

/*Tags*/
.Tag {
    background: #7805e4;
    color: #1b1b1b;
}

/*Button*/
.ButtonGroup.Primary .Button, .Button.Primary, .ButtonGroup.Action .Button, .Button.Action {
    background-image: -webkit-gradient( linear,left bottom,left top,color-stop(0,#381a58),color-stop(1,#1b1b1b) );
    text-shadow: 0 -1px 0 #fff;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.3), inset 0 -1px 2px rgba(0,0,0,.2);
    -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,.3),inset 0 -1px 2px rgba(0,0,0,.2);
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,.3), inset 0 -1px 2px rgba(0,0,0,.2);
    border-color: #e9edff;
    color: #fff;    
}


/*Profile Pic Transparency*/
.ProfilePhoto {
    background-color: transparent;
}

/*Other*/
.CommentForm .WriteButton, .CommentForm .PreviewButton, .CommentForm .DraftButton {
    margin: 0 6px;
}
.btn-default, .Button, .ActionBlock .React {
    color: #333;
    background-color: #fff;
    border-color: #ccc;
}
}
