css:
@-moz-document domain("mmos.com") {
body {
	color: #eee;
	background-color: #111;
}
#wrap, #backgroundgray, #topnavi-wrap {
	background-color: #111;
}
.widget {
	box-shadow: 0 0 4px #0c0c0c;
	border: 1px solid #0c0c0c;
	background: #0c0c0c;
}
.widget-category-posts .small-post {
	background: #0c0c0c;
}
.entry-title a:link, .entry-title a:visited,
.widget a:link, .widget a:visited,
.entry h1, .comment h1, .entry h2, .comment h2, .entry h3, .comment h3, .entry h4, .comment h4, .entry h5, .comment h5, .entry h6, .comment h6,
.post-pagination a:link, .post-pagination a:visited,
.archive-title,
input, textarea {
	color: #eee;
}
.entry-meta a:link, .entry-meta a:visited,
.widget ul {
	color: #bbb;
}
a, a:link, a:visited,
.entry-title a:hover, .entry-title a:active,
.post-pagination a:hover{
	color: #ff7575;
}
.widgettitle {
	background: #650000;
	color: #eee;
}
/* SEARCH */
.search-form .search-field {
	background: #0c0c0c;
	border: 1px solid #222;
	color: #eee;
}
/* HIGHEST RATED GAMES */
li.top10 {
	border-bottom: 1px solid #222;
}
/* slider */
#frontpage-slider .zeeslide .slide-entry {
	color: #eee;
	background: #0c0c0c;
	border-top: 10px solid #650000;
}
#frontpage-slider .zeeslide .slide-title a:link, #frontpage-slider .zeeslide .slide-title a:visited {
	color: #eee;
}
.frontpage-slider-controls .zeeflex-control-paging li a.zeeflex-active {
	background: #650000;
}
#frontpage-slider-wrap:hover .frontpage-slider-controls a:hover {
	background-color: #860000;
}
.frontpage-slider-controls .zeeflex-control-paging li a {
	background: #0c0c0c;
}
.frontpage-slider-controls .zeeflex-control-nav li {
	background: #111;
}
/* navbar */
#navi-wrap, .social-icons-menu li a:hover, #topnav-toggle:hover, #topnav-menu a:hover, #topnav-menu ul, .sidebar-closing-toggle:hover, .sidebar-closing-toggle:active, .sidebar-closing-toggle:focus {
	background-color: #650000;
}
#mainnav-menu a:hover {
	background: #860000;
}
#mainnav-menu ul a:hover {
	color: #eee;
	background: #860000;
}
#mainnav-menu ul a:link, #mainnav-menu ul a:visited {
	color: #eee;
}
#mainnav-menu ul {
	background: #0c0c0c;
	box-shadow: 0 0 2px #444;
}
/* FREE MMO AND MMORPG GAME CATEGORIES */
.widget > div > p > a > img {
	border-radius: 10px;
}
/*  */
.breadcrumbs,
.type-post, .type-page, .type-attachment,
.post-pagination,
.page-header {
	color: #eee;
	box-shadow: 0 0 4px #0c0c0c;
	border: 1px solid #0c0c0c;
	background: #0c0c0c;
}
/* article */
.page-title, .entry-title {
	color: #eee;
	border-bottom: 4px solid #222;
}
.entry h2, .comment h2 {
	border-bottom: 3px #222 solid;
}
.entry-meta {
	color: #eee;
	background: #101010;
}
strong, b {
	color: #fff;
}
blockquote {
	background: #1e1e1e;
	border-left: 10px solid #303030;
}
/* /preview */
.entry tr td {
	border-top: 1px solid #151515;
}
.entry table {
	border: 1px solid #151515;
}
tbody, th, td, tr {
	background-color: rgba(0, 0, 0, 0.1);
}
/* gallery */
ul.postTabs {
	border-bottom: 1px solid #222 !important;
}

.ngg-singlepic {
	background-color: rgba(255, 255, 255, 0.02);
}
.ngg-gallery-thumbnail img {
	background-color: rgba(255, 255, 255, 0.02);
	border: 1px solid #222;
}
.ngg-gallery-thumbnail img:hover {
	background-color: rgba(255, 255, 255, 0.1);
}
#fancybox-overlay {
	background-color: #000!important;
}
/* tagged with */
.postinfo .meta-category a {
	color: #eee;
	background: rgba(255, 255, 255, 0.05);
}
.postinfo .meta-category a:hover, .postinfo .meta-category a:active {
	background: #650000;
}
/* /gemelist */
.gamelist_table tr:nth-of-type(odd) {
	background: rgba(255, 255, 255, 0.05);
}
.gamelist_table td, th {
	border: 1px solid #252525 !important;
}
/* /music */
#wpns-search-wrap #wpns_search_form #wpns_search_input {
	border: 1px solid #222;
	background-color: #0c0c0c;
}
#wpns-search-wrap #wpns_results_box {
	box-shadow: 0 0 10px -3px #222;
	-moz-box-shadow: 0 0 10px -3px #222;
	-webkit-box-shadow: 0 0 10px -3px #222;
	-o-box-shadow: 0 0 10px -3px #222;
	background-color: #0c0c0c;
	border: 1px solid #222;
}
#wpns-search-wrap #wpns_results_box h4 {
	border-bottom: 2px solid #222;
}
img[alt="Random Soundtrack Button"] {
	border-radius: 30px;
}
.type3.one-third {
	border: 1px solid #222;
}
/* footer */
#footer, #footer-widgets-bg, #footer-widgets .widget {
	background: #0c0c0c;
}
#footer-widgets .widgettitle {
	background: #650000;
}
#footer, #footer-widgets .widgettitle {
	color: #eee;
}
#footer-widgets .widget ul, #footer-widgets .widget div {
	color: #bbb;
}
}
