@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix(https://eksisozluk.com) {

body {
    background-color: #F4F1EA;
    font-family: 'Source Sans Pro',sans-serif;
    font-size: 14px;
    font-weight: normal;
    color: black !important;
    line-height: 1.618;
}
  
   body > header {
    min-width: 300px;
    border-top: 4px solid #81c14b;
    background-color: #F4F1EA;
    border-bottom: 1px solid #e8e7e3;
}
  
  html.no-touch .topic-list li > a:hover {
    text-decoration: none;
    background-color: #DDD4C7;
}
  
  #entry-item-list a, #draft-content a, #disambiguations a {
    color: green;
}
  
  input, select, textarea {
    color: black;
    background-color: #F4F1EA;
    font-family: 'Source Sans Pro',sans-serif;
}
  
  .dark-theme #top-bar .eksico {
    fill: black;
}
  
  a {
    color: black;
    text-decoration: none;
    cursor: pointer;
    -ms-touch-action: manipulation;
    touch-action: manipulation;
}
  
  .sub-title-menu > a, .sub-title-menu > div > a, .sub-title-menu > div > .dropdown > a {
    color: black;
}
  
  html.no-touch a.more-data:hover {
    text-decoration: none;
    background-color: #F4F1EA;
    color: black;
}
  
.dark-theme #sub-navigation > ul > li > a {
    color: black;
}
  
  .dark-theme #top-navigation > ul > li > a {
    color: black;
}
  
  .topic-list li > a {
    display: block;
    padding: 10px 0;
    -ms-word-wrap: break-word;
    word-wrap: break-word;
    color: black;
}
  
  #content-body {width: 98%;}
  #logo a {display: none;}
  #topic > h1 {display: none;}
  h1 {display: none;}
  .click-to-play {display: none;}
  #video object, #embedded-player, #video-embed-container {display: none;}
    
}
