css:
@-moz-document domain("1movies.to"), domain("1movies.se"), domain("1movies.is")
{html,
body {
    background-color: #000000 !important;
}
.container #menu .bgLayout {
	background: #1c1c1c;
}
#movie_decription.new_view div#player_in_tab {
    background: #1c1c1c;
}
#movie_decription.new_view .cd-tabs-content li.content_tab:not(#general) {
    background: #1c1c1c;
}
#movie_decription.new_view div#player_in_tab .add_padd_new {
    background: #1c1c1c;
}
#movie_decription.new_view div#player_in_tab .media_media_wrap {
    background: #1c1c1c;
}
#movie_decription.new_view .cd-tabs nav {
    background: #1c1c1c;
}
#movie_decription.new_view .cd-tabs-navigation a{
    background: #1c1c1c;
}
.cd-tabs-navigation a.selected {
     background-color: #1a1a1a!important;
}
.container #content .media_wrap .right .tabs_wrap .tabs {
    background: #1c1c1c;
}
.container #content .media_wrap .right .tabs_wrap .tabs li.tab.active {
    background: #1a1a1a!important;
}
.cd-tabs {
    background-color: #1a1a1a;
}
.container #content #episodes .episode {
    background: #1a1a1a;
}
.mCustomScrollBox {
    background: #1a1a1a;
}
.container #content #movie_decription.new_view .main_desc {
    background: #1a1a1a;
}
#addthis_wrapper {
    background: #1a1a1a;
}
.container #content #comments {
    background: #1a1a1a;
}
.container #content .keywords {
    background: #1a1a1a;
}
.rows_des.actor_critic_wrap .actor_critic_block {
    background: #000000;
}
.rows_des .actor_critic_block.actor h2 {
    background: #000000;
}
.rows_des .actor_critic_block.actor .field_box {
    color: #4b4b4b
}
.container #content .offers .flex_offers .offer_box .offer_placeholder {
    background: #000000;
        border: 1px dashed #1a1a1a;
}
#addthis_wrapper.sh {
     background: #000000;
}
.container #unsubsribe_news, .container #subsribe_news, .container #news {
     display: none;
}
#watch_details {
    background: #1a1a1a;
}
.container #header .search #search {
    color: #354862;
    }
.embed {
     background: #1a1a1a;
}
#comments .comments_body .comment {
    border: 1px solid #2c2c2c;
    color: #4b4b4b;
    }
#comments .comments_body .comment .inline.body .c_text_time {
    color: #354862;
    }
#content .superWrap.margin-bottom, #content .ad_wrap.margin-bottom {
    display: none;
    }
#content .superWrap.desk_option .superButton.left_one, .ad_wrap.desk_option .ad_button.left_one {
    display: none;
    }
#content .superWrap.desk_option .superButton.top_line, #content .ad_wrap.desk_option .ad_button.top_line {
    display: none;
    }
}
