css:
@-moz-document domain("hillydilly.com") {
.app {
	background: black;
}
#content {
	background: #282828;
}
.appFooter {
	background: #282828;
}
.primaryNav__links a {
	display: block;
	position: relative;
	overflow: auto;
	text-decoration: none;
	height: 38px;
	line-height: 38px;
	margin: 0 10px;
	padding: 0 10px;
	font-size: 14px;
	font-weight: 500;
	color: white;
	border-bottom: 1px solid transparent;
	border-radius: 2px;
}
.appHeader {
	background: #282828;
}
.appPlayer__title {
	font-weight: 600;
	font-size: 12px;
	position: absolute;
	top: 9px;
	left: 265px;
	right: 230px;
	color: #ffffff;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	font-family: 'Open Sans', sans-serif;
}
.icon-magnifying-glass {
	height: 50px;
	color: rgba(255, 255, 255, 0.84);
	background: 0 0;
	outline: 0;
}
.appSearch--player {
	position: absolute;
	right: 0;
	top: 0;
	width: 50px;
	background: #282828;
	z-index: 5;
	-webkit-transition: all .1s ease-out;
	-moz-transition: all .1s ease-out;
	-ms-transition: all .1s ease-out;
	-o-transition: all .1s ease-out;
	transition: all .1s ease-out;
	transition-delay: .3s;
}
.appPlayer__heart {
	float: left;
	color: rgba(255, 255, 255, .81);
	background: #282828;
	outline: 0;
	-webkit-transition: color .2s ease-out;
	transition: color .2s ease-out;
}
.appPlayer__controls button {
	display: block;
	float: left;
	width: 50px;
	height: 50px;
	margin: 0;
	padding: 0;
	border: none;
	outline: 0;
	background-color: transparent;
	color: rgba(255, 255, 255, 0.84);
	font-size: 24px;
}
.primaryNav__links a.active,
.primaryNav__links a:hover {
	background: #212121;
	border-color: rgba(0, 0, 0, .1);
	color: #ca1f1f;
}
.appPlayer__volumeIcon {
	position: absolute;
	top: 0;
	right: 50px;
	line-height: 50px;
	text-align: center;
	color: rgb(255, 255, 255);
	background: 0 0;
}
html,
body,
.primaryNav__userName,
.trackItem__heart,
.trackItem__menu--popular,
.trackItem__link,
.trackItem__title,
.trackItem__artist,
.appNav__tab .icon,
.appNav__tab .icon,
.appNav__tabs,
.playlistItem__tags a,
.playlistItem__title,
.playlistItem__attribution,
.appSidebar__header,
.appSearch__emptyHistory,
.appSearch__searchFiller,
.appSidebar__headerButton,
.singlePost__sharingTitle,
.singlePost__sharingCountTitle,
.singlePost__shareButton,
.trackItem__meta,
.trackItem__menu,
.icon-controller-play:before,
.singlePost__nextTitle,
.singlePost__nextArtist,
.trackItem__artist--minimal,
.trackItem__title--minimal,
.singlePost__title,
.singlePost__date,
.singlePost__artistLinks,
.singlePost__tags a,
.singlePost__tagTitle,
.appFooter__keyLine,
.appFooter__mission,
.appFooter__keyboardTrigger,
.appFooter__socialLinks a,
.primaryNav__userLinks a,
.user__name.small,
.user__location.small,
.primaryNav__links .icon:before,
.primaryNav__userName a,
.primaryNav__userLink a,
.trackItem--charts__rank,
.trackItem--horizontal__title,
.trackItem--horizontal__artist,
.trackList--horizontal__lastUpdated,
.trackList--horizontal__lastUpdated,
.icon,
.postItem__title,
.postItem__artist,
.postItem__attribution,
.postItem__excerpt,
.postItem__specialIcon discovery,
.pageEnd__promoLink,
.popularItem__date,
p,
.itemList__itemLink,
.listHeader__title,
.emptyState {
	color: white !important;
}
.sc-logo-black {
	background-image: url("https://i.imgur.com/6c1BAtK.png");
}
.playlistNav__searchField {
	margin: 0;
	margin-top: 9px;
	padding: 0 13px;
	height: 31px;
	font-size: 13px;
	box-shadow: none;
	outline: 0;
	border-top-left-radius: 15px;
	border-bottom-left-radius: 15px;
	border: none;
	color: #232323;
	background: #424242;
	display: block;
	float: left;
}
.playlistNav__searchButton {
	height: 29px;
	width: 37px;
	margin: 10px 30px 0 0;
	padding: 0;
	border-top-right-radius: 15px;
	border-bottom-right-radius: 15px;
	border: none;
	background-color: #F0F2F5;
	color: #B1B1B1;
	font-size: 20px;
}
.appSearch__searchField {
	height: 50px;
	width: 100%;
	padding: 0 30px;
	margin: 0;
	border: 0;
	background: #404040;
	box-sizing: border-box;
	outline: 0;
}
.playlistNav__searchButton {
	height: 29px;
	width: 37px;
	margin: 10px 30px 0 0;
	padding: 0;
	border-top-right-radius: 15px;
	border-bottom-right-radius: 15px;
	border: none;
	background: #282828;
	font-size: 20px;
}
.singlePost__headWrapper {
	position: relative;
	width: 100%;
	height: 340px;
	background: #282828;
	box-shadow: 0 1px 2px rgba(0, 0, 0, .1);
}
.singlePost__sharing {
	background: #282828;
}
.post__tracks {
	background: #212121;
}
.trackItem__recomm {
	right: 50px;
	background-image: url("https://i.imgur.com/OWCRJfi.png") !important;
	background-repeat: no-repeat;
	background-position: center;
}
.comments__textBox {
	display: block;
	width: 100%;
	outline: 0;
	border: none;
	resize: none;
	background-color: #3a3a3a;
	color: #212121;
	padding: 12px;
	margin: 0;
	font-size: 14px;
	border-radius: 4px;
	box-sizing: border-box;
}
.singlePost__artistLinks li {
	display: inline-block;
	margin-right: 13px;
	line-height: 26px;
	font-weight: 600;
	font-size: 12px;
	color: rgba(255, 255, 255, 0.84);
	text-transform: capitalize;
}
.primaryNav__userName {
	line-height: 72px;
	color: rgb(255, 255, 255);
	font-weight: 300;
	font-size: 15px;
}
.primaryNav__userLink a {
	display: block;
	position: relative;
	width: 100%;
	height: 100%;
	line-height: 34px;
	text-decoration: none;
	color: #ffffff;
	font-size: 16px;
	box-shadow: 1px 0 0 rgba(0, 0, 0, .09);
}
.singlePost__date abbr {
	color: #d2d2d2;
	border: none;
	text-decoration: none;
}
.singlePost__tags a {
	text-decoration: none;
	border: 1px solid #E1E1E1;
	padding: 2px 4px;
	color: #ffffff;
	margin-right: 3px;
	border-radius: 4px;
}
.singlePost__editorial a {
	text-decoration: underline;
	color: #757575;
}
.playlistItem__stats strong {
	font-weight: 400;
	color: #ffffff;
}
.playlistItem__stats {
	position: absolute;
	right: 0;
	top: 36px;
	margin: 0;
	padding: 0;
	list-style: none;
	font-size: 15px;
	font-weight: 300;
	color: #b3b3b3;
}
.pageEnd--detailed {
	position: relative;
	background: rgba(240, 242, 245, 0.07);
	line-height: 60px;
	height: 60px;
	padding: 0 30px;
	overflow: hidden;
}
.popularItem__date {
	position: absolute;
	left: 267px;
	top: 90px;
	font-size: 13px;
	opacity: .65;
	color: black;
}
.playModes__repeatAll,
.playModes__shufflePlay {
	position: relative;
	display: block;
	cursor: pointer;
	height: 40px;
	text-align: center;
	background: #666667;
	border-radius: 2px;
	font-size: 15px;
	line-height: 40px;
	-webkit-transition: color .1s ease-out;
	-moz-transition: color .1s ease-out;
	-ms-transition: color .1s ease-out;
	-o-transition: color .1s ease-out;
	transition: color .1s ease-out;
}
.user__settingsButton,
.user__unfollowButton.unfollow {
	background: #4e4e4e;
	border-color: #8a8a8a;
	color: #ffffff;
}
.userPlaylists__create {
	background: #4e4e4e;
	border-color: #8a8a8a;
	color: #ffffff;
}
.appPlayer__heart.liked {
	color: #ca1f1f !important;
}
.postItem__heart.liked {
	color: #ca1f1f !important;
}

.listHeader__icon.hd-discovery {
    background-image: url(https://i.imgur.com/nBomYYi.png);
}
.listHeader__icon.premiere {
    background-image: url(https://i.imgur.com/fLiiwzf.png);
}
.listHeader__icon.hd-selection {
    background-image: url(https://i.imgur.com/Cd5Ti1Y.png);
}

}
