css:
@-moz-document domain(disk.yandex.ru) {

.button2_view_default.button2_tone_grey-blue.button2_action_yes, .button2_view_default.button2_tone_grey-blue.button2_theme_action {
    color: #000;
}
.button2_view_default.button2_tone_grey-blue.button2_action_yes.button2_hovered_yes, .button2_view_default.button2_tone_grey-blue.button2_theme_action.button2_hovered_yes {
    color: #000;
}
.button2_view_default.button2_action_yes.button2_tone_grey-blue:before {
    background-color: #E8B132;
}
.button2_view_default.button2_tone_grey-blue.button2_action_yes.button2_hovered_yes:before, .button2_view_default.button2_tone_grey-blue.button2_theme_action.button2_hovered_yes:before {
    background-color: #EFBA3F;
}
.button2_view_default.button2_action_yes.button2_focused_yes.button2_tone_grey-blue:before {
    background-color: #E8B132;
}
.button2_view_default.button2_action_yes.button2_hovered_yes.button2_tone_grey-blue:before {
    background-color: #EFBA3F;
}
.button2_view_default.button2_action_yes.button2_pressed_yes.button2_tone_grey-blue:before {
    background-color: #E7AF30;
}
.button2_view_default.button2_hovered_yes.button2_tone_grey-blue:before, .button2_view_default.button2_tone_grey-blue:before {
    background-color: #fcfdfd;
}
.info-space__indicator-bar {
    background-color: rgba(35, 177, 26, 0.86);
}
.b-progressbar__fill {
    background-color: #F5CD3E;
    background-image: linear-gradient(-45deg,rgba(148,108,108,.25) 25%,transparent 25%,transparent 50%,rgba(148,108,108,.25) 50%,rgba(148,108,108,.25) 75%,transparent 75%,transparent);
}
.file-icon_dir.file-icon_size_s:after {
    -webkit-filter: saturate(1.2);
}
.software-header-promo_win .link__icon {
    color: #e0ac39;
}
.yaplus__link.link, .yaplus__link.link.link_hovered_yes {
    color: #4e4638;
}
.yaplus_available_yes .yaplus__badge-main {
    box-shadow: 0 1px 3px 0 rgba(124,98,16,.95);
    background-color: #e7af30;
}
.yaplus_available_yes .link_hovered_yes .yaplus__badge-main {
    background-color: #efba3f;
}
.yaplus.yaplus_available_yes .yaplus__badge:after {
    background-image: linear-gradient(-180deg,#e5dbbd 0,#e3e6db 100%);
}
.resources-action-bar_theme_blue {
    color: #000;
    background-color: #dea43c;
}
.button2_view_default.button2_tone_transparent.button2_theme_action, .button2_view_default.button2_tone_transparent.button2_theme_normal {
    color: #000;
}
.button2_theme_clear-inverse {
    color: #000;
}

}
