css:
@-moz-document domain(vk.com) {

#page_header_cont, .im-right-menu {
   transition: top 0.5s ease-in-out 0s;
}
#side_bar_inner, #page_body, ._im_dialogs_search, .im-page--header-chat {
   transition: margin-top 0.5s ease-in-out 0s;
}

#pv_box {
    overflow: hidden;
}
#pv_photo {
    transition: width 0.5s linear 0s;
}
.pv_photo_wrap {
    white-space: nowrap;
}
.pv_narrow_column_wrap {
    float: none !important;
    white-space: normal;
    position: relative;
    left: 0px;
    transition: left 0.5s linear 0s;
}
.pv_tc_wrap {
    position: absolute;
    right: 0;
    bottom: 0;
    padding: 50px 25px 25px 50px;
    z-index: 2;
}
.pv_tc_wrap:hover .pv_tc_btn {
    opacity: 0.7;
}
.pv_tc_wrap:hover .pv_tc_btn:hover {
    opacity:1;
}
.pv_tc_wrap .pv_tc_btn {
    padding: 10px;
    background-color: rgba(0,0,0,0.5);
    border-radius: 3px;
    opacity: 0;
    cursor: pointer;
    transition: opacity 200ms ease;
}
.pv_tc_wrap .pv_tc_btn div {
    background-image: url('https://vk.com/images/icons/fullscreen_photo.png');
    height: 18px;
    width: 18px;
    transform: rotate(45deg);
}

.audio_page_player .audio_page_player_title_performer,
.audio_page_player .audio_page_player_title_song {
    max-width: 90% !important;
}

#download_audio_button {
    width: 18px;
    height: 16px;
    display: block;
    top: 6px;
    background-image: url('https://vk.com/images/icons/pv_actions.png');
    background-position: 0px 0px;
    float: right;
    opacity: 0;
}
.top_audio_player .top_audio_player_title {
    max-width: 90% !important;
}
.head_nav_item_player:hover #download_audio_button {
    opacity: 0.68;
}
.head_nav_item_player {
    overflow: hidden;
    height: 42px;
}

body.no_select::selection, body.no_select *::selection {
    background: rgba(0, 0, 0, 0);
}

.narrow_column_wrap .narrow_column.fixed {
    transition: opacity 0.5s ease-in-out 0s;
}
.narrow_column_wrap .narrow_column.hidden {
    opacity: 0;
}

.post_full_like {
    float: right !important;
}
.post_full_like_wrap .reply_link_wrap {
    margin-right: 0px !important;
    float: left !important;
}
.post_share {
    margin-right: 0px !important;
}
.wall_module .post_views {
    padding: 6px 16px 6px 6px !important;
}
.processed a.page_post_thumb_wrap,
.processed span.page_post_thumb_wrap {
    margin: 0 6px 6px 0 !important;
}
.page_post_sized_thumbs.processed a.page_post_thumb_last_row,
.page_post_sized_thumbs.processed span.page_post_thumb_last_row {
    margin-bottom: 0 !important;
}
.page_post_sized_thumbs.processed a.page_post_thumb_last_column,
.page_post_sized_thumbs.processed span.page_post_thumb_last_column {
    margin-right: 0 !important;
}
.wall_module .media_desc {
    margin: 2px 0px !important;
}
.post_thumbed_media .media_desc_soft {
    margin: 0 5px 5px 0 !important;
}

.im-page .im-page--header-more .ui_actions_menu_icons {
    background-position: 2px center !important;
}

.nim-dialog.nim-dialog_unread-out.nim-dialog_muted:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread {
    display: none !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--unread {
    right: 6px !important;
}
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread {
    bottom: 12px !important;
}
.nim-dialog .nim-dialog--content {
    padding-right: 20px !important;
}
.nim-dialog .nim-dialog--cw {
    border-radius: 4px !important;
    box-sizing: border-box !important;
    height: 47px !important;
    margin: 8px 0px !important;
    padding: 3px 7px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--cw {
    border-radius: 3px !important;
    box-sizing: border-box !important;
    height: 51px !important;
    margin: 10px 0px !important;
    padding: 1px 0px 0px 6px !important;
}
.nim-dialog .nim-dialog--name, .nim-dialog .nim-dialog--date {
    margin-top: 0px !important;
}
.nim-dialog .nim-dialog--date {
    top: 3px !important;
    right: 4px !important;
}
.nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--date {
    display: block !important;
}
.nim-dialog.nim-dialog_failed .nim-dialog--unread, .nim-dialog.nim-dialog_unread .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread {
    margin-top: -18px !important;
}
.nim-dialog .nim-dialog--unread {
    right: 3px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--name, .nim-dialog.nim-dialog_classic .nim-dialog--date {
    margin-top: 6px !important;
}
.nim-dialog:not(.nim-dialog_classic) .nim-dialog--name, .nim-dialog:not(.nim-dialog_classic) .nim-dialog--date {
    margin-top: 3px !important;
    margin-bottom: 1px !important;
}
.nim-dialog .nim-dialog--date {
   right: 6px !important;
   top: 3px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--date {
   top: 0px !important;
}
.nim-dialog .nim-dialog--close {
   right: -16px !important;
   top: -3px !important;
   margin-top: 2px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--close {
   right: -14px !important;
   margin-top: 4px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--unread {
   right: 6px !important;
   bottom: 8px !important;
}
.nim-dialog {
    padding-left: 20px !important;
    border-top: 1px solid #f6f6f6 !important;
}
.nim-dialog .nim-dialog--content {
    border: 1px solid transparent !important;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.im-page--messages-search:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.im-page--messages-search:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog:before,
.nim-dialog:not(.nim-dialog_deleted):hover+.im-page--messages-search:before,
.nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog:before,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.im-page--messages-search .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.im-page--messages-search .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.im-page--messages-search .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.im-page--messages-search .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted):hover+.im-page--messages-search .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted):hover+.im-page--messages-search .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog .nim-dialog--content,
.nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog .nim-dialog--photo {
    margin-top: 0px !important;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog,
.nim-dialog:not(.nim-dialog_deleted):hover+.im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog {
    border-top: 1px solid #eaebef !important;
}
.im-page .im-page--header-more .ui_actions_menu_icons {
    background-position: center 78% !important;
}
.online:after {
    bottom: -4px !important;
    right: -4px !important;
}
.nim-peer .nim-peer--photo-w {
    border-radius: 4px !important;
}
.nim-peer .nim-peer--photo .im_grid>img, .nim-peer .nim-peer--photo>img {
    border-radius: 4px !important;
}
.wall_module .reply_img {
    border-radius: 4px !important;
}
.module_body .people_cell_img {
    border-radius: 4px !important;
}
.page_list_module .thumb, .page_list_module .cell_img {
    border-radius: 4px !important;
}
.friends_photo_img {
    border-radius: 4px !important;
}
.friends_user_row .online.mobile:after {
    bottom: -1px !important;
    right: -1px !important;
    border-radius: 3px 0px 0px 0px !important;
    border: 2px solid #fff !important;
}
.friends_photo_wrap .ui_zoom_inner {
    border-radius: 4px !important;
}
.group_row_photo, .group_row_img {
    border-radius: 4px !important;
}
.like_tt_owner, .poll_tt_owner {
    border-radius: 3px !important;
}
.like_tt_image, .poll_tt_image {
    border-radius: 3px !important;
}
.im-mess {
    padding: 11px 43px 4px 0px !important;
}
.im-mess:not(:first-child) .im-mess--actions {
    margin-top: -3px;
}
.im-mess:not(:first-child) {
    padding-top: 5px !important;
}
.im-mess .im-mess--check {
    top: 50% !important;
    margin-top: -8px !important;
}
.wddi_thumb {
    overflow: visible !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--name,
.nim-dialog.nim-dialog_classic .nim-dialog--date {
    margin-top: 6px !important;
    height: 16px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--text-preview {
    margin-top: -2px !important;
}
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .nim-dialog--text-preview {
    margin-top: -5px !important;
}
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .im-prebody {
    margin-top: 3px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--inner-text,
.nim-dialog.nim-dialog_classic.nim-dialog_muted .nim-dialog--inner-text {
    padding: 3px 0 2px 2px !important;
}
.nim-dialog.nim-dialog_classic .nim-dialog--inner-text,
.nim-dialog.nim-dialog_classic:hover .nim-dialog--inner-text {
    background: none !important;
}
.nim-dialog_unread-out:not(.nim-dialog_selected) .nim-dialog--cw {
    background: rgba(147, 173, 200, 0.2) !important;
}
.nim-dialog:not(.nim-dialog_classic).nim-dialog_unread .nim-dialog--cw {
    background: rgba(147, 173, 200, 0.2) !important;
}
.im-prebody {
    margin-right: 0px !important;
    vertical-align: top !important;
    height: 12px !important;
}
.nim-dialog:not(.im_group_chat) .im-prebody:before {
    content: 'Вы: ';
    display: inline-block;
    height: 14px;
    vertical-align: top;
}
.nim-dialog.im_group_chat .im-prebody>span {
    display: inline-block;
    height: 14px;
    vertical-align: top;
}
.nim-dialog .im-prebody img {
    display: none;
}
.nim-dialog.nim-dialog_classic.im_group_chat .nim-dialog--inner-text,
.nim-dialog.nim-dialog_classic.nim-dialog_muted.im_group_chat .nim-dialog--inner-text {
    padding-left: 0px !important;
}
.nim-dialog.im_group_chat .im_prebody:before {
    content: '';
    display: none;
}

}
