css:
@-moz-document domain("learn.javascript.ru") {
body, body{
    background: #111;
    color: #bababa!important;
}
.sidebar__content, .line-numbers-rows>span:before, .line-numbers .line-numbers-rows:after
{
    background:none;
}
p, .subscribe__line, .job-list .job__properties, .post-content .post-message p, .sitetoolbar__secondary-link
{
    color: #bababa!important;
}
h1,h2,h3, .main h1 a, .main h1 code, .main h2 a, .main h2 code, .main h3 a, .main h3 code, .main h4 a, .main h4 code, .course-info .course-info__title, .tab-conversation.active>a, .post-content .post-message, .tutorial-map-list-two__link, .main ul>li:before
{
    color:#F0f0f0!important;
}
.course-info, .course-search, .sitetoolbar, .page-footer, .sidebar_sticky-footer .sidebar__inner, .lessons-list, pre[class*=language-], .summary, .courses-features__features, .courses-programm-register, .courses-feedback-slides__review, .courses-faq ul.courses-faq__questions
{
        background: #222;
}
.course-info .course-info__header, .sitetoolbar__dropdown-body, .sitetoolbar__dropdown-body:before, .line-numbers .line-numbers-rows, .main code, .tutorial-map__filter, .courses-features h2.courses-features__title, .tabbed-pane li.tabbed-pane__tab{
        background: #333;
}
.tutorial-map-overlay
{
    background: #222222fa;
}
.tutorial-map-overlay .tutorial-map:after
{
        background: linear-gradient(180deg,hsla(0,0%,100%,0) 0,#222 80%);
}
.lessons-list, .tabbed-pane__body
{
    border: 4px solid #333;
}
.participants-logos__slider-i:after
{
    background: linear-gradient(270deg,#111 0,hsla(0,0%,100%,0));
}
.subscribe_fancy{
background: #222 url(https://learn.javascript.ru/img/subscribe_bg.svg) 0 80% no-repeat;
}

::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #000;
    width: 10px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c;
}
}
