css:
@-moz-document domain("php.net") {
html {
    background: #2e353a;
    color: #d8d8d8;
}
a:hover,
a:focus,
#layout-content a:hover,
#layout-content a:focus {
    color: #8ec153;
    border-color: #4CAF50;
    outline: 0;
}
.navbar {
    border-color: #0d6fbd;
    background: #2196F3;
}
.navbar .nav .active > a,
.navbar .nav .active > a:hover,
.navbar .nav .active > a:focus {
    color: #fff;
    background-color: #0d6fbd;
}
#layout {
    position: relative;
}
#layout > aside.layout-menu {
    position: fixed;
    right: 20px;
}
#layout-content:not(:only-child) {
    border-right: .25rem solid rgba(7, 144, 255, 0.5);
}
h1.refname {
    color: #4CAF50;
}
.title a,
.title {
    color: #3F51B5;
}
.layout-menu ul.child-menu-list a {
    border-color: #9E9E9E;
    color: rgba(236, 236, 236, 0.6);
}
.layout-menu ul.child-menu-list li.current a {
    color: #dedede;
    border-bottom-color: #dedede;
}
}
