.loginLinkZ:hover,.loginLinkS:hover{padding:10px;text-decoration:none!important;background:rgba(5,65,68,0.76);}
.search{margin:0px -265px 0px 0px;width:300px;background:rgba(125,5,5,0.5)!important;}
#searchsuggestions{background-color:#fff0;overflow-y:scroll;height:544px!important;border:0px!important;}
#searchsuggestions p {color: #fff!important;}
#searchsuggestions a{background-color:#4c1313;border:0px!important;}
#searchsuggestions a:hover{background:rgb(0,0,0);}
.searchheading,.seperator{background:#000!important;}
#wrap{margin: 10px 269px 10px 0px!important;}
.banerTop,#buttonLike,.present,#banner,#vk_groups,.gwd-page-container,.interDub div.interDubBg,#stext,.shortstoryFuter,.skrin legend,#shortstoryContentTegi,.banerTopTwo,.loginExit,#shortstoryContentTegi+div,.fastPunkt,.functionPanel+a{display: none!important;margin: 0px !important;width: 0px !important;}
div.imgOngoing img{background:rgba(0,0,0,0);}
div.imgOngoing span{font-size: 19px;color:#f00!important;background:rgba(0, 0, 0, 0.76);}
div.menu{background:linear-gradient(to bottom,#000000 0%,#fb0000 100%)!important;;border-radius: 14px;width: 1274px!important;}
.menu ul#topnav li span.sar{background:linear-gradient(to bottom,#2b9fda 0%,#061b3a 100%)!important;}
#blok_1,#blok_2,#blok_3,#blok_4,#blok_5,#blok_6,#blok_7,#blok_8,#blok_78,#blok_19,#blok_20{border-radius:13px;background:rgba(90,3,4,0.86)!important;padding: 9px 2px;}
#blok_20{width:100px;}
ul#topnav li span.sar span a{padding:3px 0px 7px 0px;color: #00cab7;font-size: 16px;text-decoration:none;border:0px;text-align:center;}
ul#topnav li span.sar span a:hover{background: rgba(0, 0, 0, 0.63);border-radius:6px;}
p, span, img{color: #c77b0e!important;}
.moduleLeft{border: 0px !important;}
#linebanner{height: 0px;}
.interDub div.interDubBg{border: 1px !important;}
p,span,img{font-size: 15px;color: black;}
#leftBlock,#centerBlock{bottom: 300px;margin-top: 0;}
.menu ul#topnav li span.sar{z-index: 99999 !important;top: 50px !important;position: absolute !important;}
#futer, #ambn53991333{display: none !important;}
div.interDubBgTwo ul li a{color:#fff !important;font-size: 14px;text-shadow: none;}
body{background:url('http://i.artfile.ru/3500x2000_804851_[www.ArtFile.ru].jpg');background-repeat:no-repeat;background-size:cover;background-attachment: fixed;}
div.topLine div.login form{background: #184896;}
div.topLine div.login form #login_name, div.topLine div.login form #login_password{background: #0d8fb1;color:#000000;}
.present p{color:rgb(56, 160, 202);}
ol li{color:#868686;}
.present h1{color:#ce3d65;}
.present{border-radius: 0px;border: 1px solid #307962;}
.shortstory{box-shadow: none !important;border-radius: 10px;background:rgba(78,10,10,0.6);width: 988px;}
.shortstory .shortstoryHead h2 a, .shortstory .shortstoryHead h1 a{color:#afafaf;}
.shortstory .shortstoryHead h2, .shortstory .shortstoryHead h1{color:#ff8100;font-size:18px!important;text-shadow:0 0px!important;font-weight:bold!important;background: none;}
.shortstoryHead{width: 988px;}
.staticInfo{background: rgba(12, 20, 41, 0.5)!important;width: 988px!important;}
.userinfo{background:#750000bf;}
.unit-rating li.current-rating {background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHwAAAANCAYAAABrYAraAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsQAAA7EAZUrDhsAAABmSURBVFhH7dexCcAwDERROZC5DKkyRwbLKGndeI4sYUzSeAA1Apv7rzGqXBwSXMpn/SzQczc7rn1MMfjDbxsvRKT+ltANx1w46U6cdCyJwMUQuBgCF0PgYghcDD1cDD3ciR6OBZn9uTMyXptc4kQAAAAASUVORK5CYII=');}
.interDub div.interDubBgTwo{background:rgba(140,8,8,0.7);border:0px;border-radius:8px;}
.interDubBgTwo a{background:rgba(0,0,0,0.5)!important;}
.interDubBgTwo b{border:0px!important;}
.interDubBgTwo ul li {border-bottom:0px!important;background:rgb(128,8,8);margin: 4px 4px 4px 4px;}
.interDubBgTwo ul li:hover {background: rgb(0,0,0)!important;}
.interDubBgTwo ul li a {border-bottom:0px!important;}
.interDubBgTwo h2{border:0px!important;}
.raspis .popup{background: rgba(17, 40, 74, 0.8)!important;color: #c9de1f!important;text-shadow:none;border:0px;border-radius: 13px;}
.popup .nexttime{background: rgba(79, 101, 254, 0.5);color:black;}
#nexttime{background: rgba(79, 101, 254, 0.5);color:black;}
div.miniInfo{background: rgba(0, 0, 0, 0.5)!important;color: #2adcb6!important;border-radius: 0px!important;}
.imgRadius{border-radius: 0px;}
h4{background: #3c36a0;border-radius: 0px;}
#leftBlock{margin:0px 0px 0px 6px;}
#centerBlock{position: relative;top: 0;right: 0;bottom: 0;left: 0;margin:0px 0px 0px 0px!important;}
.interDubBgTwo b,.interDubBgTwo h2{background: linear-gradient(to bottom,#15363e 0%,#ec2222 100%)!important;border-radius: 8px 8px 0 0!important;}
p strong{color:#1ba21f!important;}
.text_spoiler,.title_spoiler{border: 1px solid #ff0000!important;background:rgba(107,3,3,0.7)!important;}
#5d18cbd165{background:#fff!important;}
.shortstoryContent{width: 964px!important;border-top:0px solid #fff!important;border-bottom:0px!important;}
.shortstoryContent h4{border-radius: 0px!important;color: #afafaf!important;background: rgba(72,20,20,0.5)!important;    margin: 0px 9px 0px 252px;text-shadow: none;}
.hide{display:none;}
}

@-moz-document url-prefix("http://animevost.org/tip/") {
.skrin{border: 0px!important;}
.skrin img{width:314px!important;height:156px!important;}
#stdplayer{border-left: 0px;}
#scrolltwo{width:886px!important;}
div#items {display: block!important;}
.epizode{background:rgba(0,35,49,0.5)!important;border-right:0px;border-top:0px;}
.epizode:hover,.active{background:rgba(88,0,0,0.9)!important;}
.prev{background:url('data:image/png;base64,')!important;width: 39px!important;height: 38px!important;}
.next{background:url('data:image/png;base64,')!important;}
#player2{width:964px;height: 540px;border:0px;background:rgba(56, 160, 202, 0.5)!important;}
#player{width:1240px;height:698px;border:2px solid #880000;background: rgba(78,10,10,0.6);}
#plist{width:1214px;padding:12px 0 0 27px;height: 125px;border:2px solid #880000;background: rgba(78,10,10,0.6);}
#scroll{width:1195px;}
#toup{background:url('data:image/jpeg;base64,');}
.series{border:0px;margin:0px 10px 0 0;}
.series span{text-shadow:0px 0px 0px;background:rgba(0,0,0,0.56);}
#todown{background:url('data:image/jpeg;base64,');}
.functionPanel{border:0px;}
.modeKino,.bottomProblem{background:#6d090996!important;}
.modeKino:hover,.bottomProblem:hover{background:#231d1db8!important;}
.modeKino{width:734px;border-right:1px solid #960707;}
#newComment{background:rgba(109,8,8,0.6);border: 0px!important;border-radius:13px!important;}
#rulesComment{border:0px;border-radius:13px!important;background:linear-gradient(to bottom,#001bfb 0%,#007084 100%)!important;background: rgba(0, 0, 0, 0.32)!important;}
#rulesComment a{text-decoration:none;text-align:center;width: 974px;}
#rulesComment:hover{background:rgba(0,0,0,0);background:rgba(0, 0, 0, 0.62)!important;}
.form_comment ul li{color:#ff0014!important;border:0px;}
#form_comment button {border:0px;background-color:#560606;border-radius:33px;}
#comment{width:984px!important;}
.commentFinal{background:rgba(60,17,17,0.82)!important;border-radius: 10px;border: none;}
.commentFinalText{background:rgba(106,8,8,0.7)!important;min-height:66px!important;border-radius: 0px;color: #c5ae8e;border: none;}
.commentFinalAva {background: rgba(0,0,0,0)!important;width: 115px;float: left;border-right:0px;}
.commentFinalData {height: 16px;border-left:0px;border-bottom:0px;color:#00b327;background:rgba(48,8,8,0.7);}
.commentFinalIt {background:rgba(48,8,8,0.7);border-top:0px;color:#00b327;border-left:0px;}
.title_quote,.quote{background:rgb(60, 8, 8)!important;border:1px solid #ab0303;border-radius:0px;}
.container{display:none;}
.shortstoryFuter{margin: 54px 0px 0px 0px;}
.q{background: rgb(83, 22, 140);position:absolute;margin: 0px 0px 0px 744px;display: block;}
.q span{width: 141px;display: block;padding: 5px 0px 4px 84px;border: ridge;color:#ff0000;cursor: pointer;}
.q span a{font-size: x-large;}
.q span:hover{background:aqua;}
.q span a:hover{font-size: x-large;}
.block_4 span,.block_4 a{background:rgb(29,80,104);padding: 10px 16px;box-shadow: 0px 0px 0px 0px;color:#fff;border-radius: 0px;text-decoration:none;}
.block_4 span,.block_4 a:hover{background:rgb(6,43,60);}
.hide{display:none;}
}

