css:
@-moz-document domain(okoun.cz) {
body{font: normal small verdana,arial,helvetica,sans-serif!important; background-color:white!important; padding:0 0 2em 0!important;}
#body {background-color:white!important; margin:0!important;}
.main .main {width:70%!important;}
.main .ctx {width:15%!important;}
.ctx div.ico-wrapper {display:none!important;}
.head {border:none!important; padding:0!important; margin:0!important; background-color:#cee3f8!important;}
h2, .ctx h3, .index h3, .expbutton .hd, .ad .label {font-family:verdana,arial,helvetica,sans-serif!important;}
.welcome, .item .content .confirm.reply {background-color:#cee3f8!important; border:none!important;}
.welcome {padding:1em!important;}
.expbutton .hd {border: 1px solid blue!important; background-color: RoyalBlue!important; border-radius: 3px;}
.pager ul li.newest, .pager ul li.newer, .pager ul li.older, .pager ul li.oldest {background-color: LightSkyBLue!important; background-image:none!important; border-radius: 3px;}
.pager ul li.curr {border: 1px dotted LightSkyBLue!important; border-radius: 3px;}
.pager ul li.newest, .pager ul li.newer, .pager ul li.older, .pager ul li.oldest {padding:3px!important;}
.pager ul li a {padding:3px 8px 3px 8px!important;}
.user.ico, .user .ico {display:none!important;}
.user {color:#369!important;}
.descr, .hpdescr {display:none!important;}
.item .content {margin-bottom: 0em!important;}
.content, .item .meta, body.userlist .main .main, body.boardlist .main .main {font-size:1em!important;}
h2 a {color:#0079d3!important;}
a:hover, .head h1 a:hover, .head .menu a:hover, .head .user a:hover {color:#888!important;}
.head .menu a, .head .menu a:visited, .head .user a, .head .user a:visited, .footer a, .footer a:visited, .item.hover .actions a, .item.hover .actions span.prev a.prev {color:#888!important;}
.item .permalink a:hover {text-decoration:none!important;}
.item .date, .item .permalink a, .item .meta a {color:#888!important;}
.listing .item {padding:0em!important;}
.item .meta {padding:0em!important; margin-bottom:0.4em!important; margin-left:2px!important;}
.room .item {border-bottom:1px solid #C0D4FF!important;}
.item .actions {margin-top:0em!important; border:none!important; margin-left:0!important;}
.item .meta .delete, .item .actions {font-size:0.8em!important;}
.main .actions {padding-top:0.4em!important; padding-bottom:0.8em!important;}
.item a.link {background-image:none!important;}
.item .meta .permalink {right: 0em!important;}
.item .content, .item .title {padding-left:0!important;}
}
