css:
@-moz-document domain("wrts.nl") {
body{
    background-color: #383c4a;
}
.navbar, .footer, .row.new-style .card-breadcrumb .breadcrumb{
    background-color: #3b4250;
    color: white;
}
.card, .card .card-sub-heading, .tabular tbody{
    background-color: #323b4d;
}
.explanation-wrapper{
    background-color: #3b4250;
}


h1,h2,h3,h4{
    color: #a1a5ae;
}
.nav-pills>li>a{
    color: white;
}
.nav-pills>li>a>img{
    filter: invert(1);
}
.nav-pills>li>a:hover{
    color: black;
}
.nav-pills>li>a:hover>img{
    filter: invert(0);
}
.dropdown-menu>li>a, .dropdown-menu .title{
    color: white !important;
}
.tabular tr * {
    color: #a1a5ae;
}
.select-all-checkbox div, .breadcrumb>.active, .share-block, .col-md-9 .card * {
    color: #a1a5ae;
}
a.title, tabular__column aligned-right * i{
    color: #03a9f4 !important;
}
.btn-block{
    color: white !important;
}
.select{
    color: #555555 !important;
}
.list-row-container{
    background-color: #00000000 !important;
}
.remove-list-link.btn {
    color: black !important;
}

.icon-question{
    color: white;
}
.brand{
    background-image: url("https://i.imgur.com/dRUwECL.png");
    background-size: 100%;
    height: 34px;
    width: 116px;
}
.brand-img{
    display: none;
}

.col-md-4 *{
    color: #a1a5ae !important;
}
.text-field, .form-control{
    background-color: #383c4a;
}
.input-group-addon, .add-image{
    background-color: #3b4250 !important;
}
.btn{
    color: white !important;
}
.configuration span{
    color: white !important;
}
.card{
    color: #a1a5ae;
}
.answergroup .head{
    background-color: #888899 !important;
}
.answergroup .count{
    border-color: #888899 !important;
}
#answer{
    color: #a1a5ae;
}

.result-container, .result-container tbody{
    background-color: #323b50 !important;
}
.dropdown-menu, .dropdown-menu *{
    background-color: #3b4250 !important;
}

.highcharts-background {
    fill: #323b4d !important;
}
.highcharts-title {
    fill: #a1a5ae !important;
}
}
