css:
@-moz-document url-prefix("http://www.rizon.net/chat"), url-prefix("https://www.rizon.net/chat"), url-prefix("http://webchat.freenode.net"), url-prefix("http://bnc.stormbit.net"), url-prefix("http://qchat.rizon.net"), url-prefix("http://webchat.oftc.net"), url-prefix("https://webchat.freenode.net"), url-prefix("https://bnc.stormbit.net"), url-prefix("https://qchat.rizon.net"), url-prefix("https://webchat.oftc.net") {
  #ircui, a.tab-selected, a.tab-hilight-activity, a.tab-hilight-speech, a.tab-hilight-us, input[type="checkbox"], input[type="submit"], select, a[target="_blank"], a.hyperlink-embedded, img, .colourline > span[class^="Xc"], iframe {
    filter: invert(100%) !important;
  }
  
  img[title="menu"] {
    filter: brightness(0%) !important;
  }

  a[target="_blank"] {
    text-shadow: 0px 0px 1px rgb(0, 0, 255);
  }
  
  .tabclose {
    filter: invert(50%);
  }
}
