css:
@-moz-document domain(fenix.tecnico.ulisboa.pt) {
/* Variables */
:root {
  --white: #eff;
  --lightest-grey: #ddd;
  --lighter-grey: #ccc;
  --light-grey: #888;
  --grey: #555;
  --dark-grey: #333;
  --darker-grey: #222;
  --darkest-grey: #111;
  
  --fg: var(--white);
  --bg: var(--darkest-grey);
  --ist-blue: #009EE2;
}


/* Style */
* {
  text-shadow: 1px 1px 1px rgba(255, 255, 255, 0.004);
}
a {
  color: var(--ist-blue);
}
a:hover,
a:focus,
a:active {
  color: var(--ist-blue);
  filter: brightness(130%);
}
pre {
  color: var(--white);
  border-width: 0px;
}
main > div > .top-lead,
header,
header .nav li > a,
.main-content,
.menu-header {
  border-color: #111;
}
pre,
.nav .open > a,
.nav .open > a:hover,
.nav .open > a:focus,
.nav > li > a:hover,
.nav > li > a:focus,
.nav-tabs {
  border-radius: 0px;
}
.nav-pills > li.active > a.item,
.nav-pills > li.active > a.item:hover,
.nav-pills > li.active > a.item:focus,
.nav-pills > li > a.item,
.site-header a,
.site-header a:hover {
  color: var(--light-grey);
}
.nav-pills > li.active > a.item,
.nav-pills > li.active > a.item:hover,
.nav-pills > li.active > a.item:focus {
  border-color: var(--darker-grey);
}
@media (max-width: 768px) {
  nav {
    border-color: var(--darker-grey);
    background-color: var(--bg);
  }
}
.date-label {
  background-color: #EE8323;
}
.badge,
.locale-changer:not(:last-of-type):after {
  color: #999 !important;
}
#content-block {
  border-right-color: var(--darker-grey);
  border-bottom-color: var(--darker-grey);
}
#content-block:after {
  background-image: -webkit-linear-gradient(right, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0), 0%, 100%);
  background-image: -o-linear-gradient(right, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0), 0%, 100%);
  background-image: linear-gradient(to left, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0), 0%, 100%);
  -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr=#00000000, endColorstr=#FF000000, GradientType=1)";
}

pre,
.nav .open > a,
.nav .open > a:hover,
.nav .open > a:focus,
.nav > li > a:hover,
.nav > li > a:focus,
.panel,
.table > thead > tr > th,
.table > tbody > tr > th,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > tbody > tr > td,
.table > tfoot > tr > td,
.table > tbody + tbody {
  background-color: var(--darker-grey) !important;
}
.nav a.rsslink {
  color: var(--dark-grey);
  padding: 10px 14px 5px 14px;
  background-color: rgba(0,0,0,0) !important;
}
.table > thead > tr > th,
.table > tbody > tr > th,
.table > tfoot > tr > th {
  background-color: var(--bg) !important;
}
.panel {
  -webkit-box-shadow: 0 1px 1px rgba(235, 255, 255, 0.05);
  box-shadow: 0 1px 1px rgba(235, 255, 255, 0.05)
}
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.form-control,
.img-thumbnail,
.infoop,
.infoop2,
.infoop2 ~ div,
.open .dropdown-toggle.btn-default,
.panel > .panel-body + .table,
.panel > .panel-body + .table-responsive,
.panel-group .panel-heading + .panel-collapse .panel-body,
.panel-group .panel-footer + .panel-collapse .panel-body,
.panel-default,
.panel-default > .panel-heading,
.panel-default > .panel-heading + .panel-collapse .panel-body,
.panel-default > .panel-footer + .panel-collapse .panel-body {
  background-color: var(--darker-grey) !important;
  border-color: var(--dark-grey) !important;
}
.panel-footer,
.panel-default > .panel-heading,
.table-hover > tbody > tr:hover > td,
.table-hover > tbody > tr:hover > th,
.table > thead > tr > td.active,
.table > tbody > tr > td.active,
.table > tfoot > tr > td.active,
.table > thead > tr > th.active,
.table > tbody > tr > th.active,
.table > tfoot > tr > th.active,
.table > thead > tr.active > td,
.table > tbody > tr.active > td,
.table > tfoot > tr.active > td,
.table > thead > tr.active > th,
.table > tbody > tr.active > th,
.table > tfoot > tr.active > th {
  background-color: var(--darkest-grey);
}
.panel-footer {
  border-color: var(--light-grey);
}
.panel-default > .panel-heading {
  color: var(--light-grey);
}

body,
main,
table,
input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
select,
textarea,
header .nav li > a,
nav#context .nav ul li a:hover,
nav#context .nav li > a,
nav#context .nav li.navheader,
.fenixedu-bar .navbar-text,
.fenixedu-bar a,
.fenixedu-bar li a,
.fenixedu-bar .navbar-brand,
.locale-changer.active,
.locale-changer.active:hover,
.tstyle1 td, .tstyle1 th,
.tstyle2 td, .tstyle2 th,
.tstyle3 td, .tstyle3 th,
.tstyle4 td, .tstyle4 th,
.tstyle5 td, .tstyle5 th,
.tstyle7 td, .tstyle7 th,
.tstyle8 td, .tstyle8 th,
.tstyle9 td, .tstyle9 th {
  color: var(--lighter-grey) !important;
}
body,
main,
#content-block,
.breadcrumb,
.container,
.modal-content,
.table > tbody > tr.clear > td {
  background-color: var(--bg) !important;
}
.fenixedu-bar {
  background-color: var(--darker-grey)
}
header .nav li.active a,
header .nav li.active a:hover,
.fenixedu-bar .navbar-nav > .active > a {
  background-color: var(--grey)
}
.fenixedu-bar .navbar-nav > .active > a:hover {
  background-color: var(--light-grey)
}
header .nav li > a {
  background-color: var(--darker-grey);
}
header .nav li > a:hover {
  background-color: var(--grey);
  color: var(--fg)
}
header .nav li.active a,
header .nav li.active a:hover {
  color: var(--darkest-grey)
}
#main-content-wrapper img {
  border-color: var(--grey) !important;
}
nav#context .nav li.navheader {
  border-color: #666;
}
nav#context .nav li > a:hover,
nav#context .nav li > a:active {
  background: var(--darkest-grey);
}
.badge {
  color: var(--fg) !important;
  background-color: var(--dark-grey) !important;
}
a.badge:hover,
a.badge:focus {
  color: var(--bg) !important;
}
a.list-group-item.active > .badge,
.nav-pills > .active > a > .badge {
  color: var(--ist-blue) !important;
  background-color: var(--bg) !important
}
.hamburger,
.hamburger:hover,
.hamburger:focus {
  border-color: var(--dark-grey)
}
.hamburger:hover,
.hamburger:focus {
  background-color: var(--dark-grey)
}
.btn-group .dropdown-toggle.btn-default ~ .dropdown-menu {
  background-color: var(--darker-grey);
  border-color: var(--dark-grey)
}
.btn-group .dropdown-toggle.btn-default ~ .dropdown-menu > li > a {
  color: var(--lighter-grey)
}
.btn-group .dropdown-toggle.btn-default ~ .dropdown-menu > li > a:hover {
  background-color: var(--dark-grey)
}
.btn-group .dropdown-toggle.btn-primary ~ .dropdown-menu > li > a,
.btn-group .dropdown-toggle.btn-success ~ .dropdown-menu > li > a,
.btn-group .dropdown-toggle.btn-info ~ .dropdown-menu > li > a,
.btn-group .dropdown-toggle.btn-warning ~ .dropdown-menu > li > a,
.btn-group .dropdown-toggle.btn-danger ~ .dropdown-menu > li > a {
  color: var(--darkest-grey)
}
blockquote,
nav#context .nav li.navheader strong,
nav#context .nav ul li a,
.infoop,
.infoop2,
.infoop2 ~ div,
.lead,
.tstyle1 select,
.tstyle2 select,
.tstyle3 select,
.tstyle4 select,
.tstyle5 select,
.tstyle7 select,
.tstyle8 select,
.tstyle9 select {
  color: var(--lighter-grey)
}
footer,
footer a,
.hamburger .icon-bar,
.locale-changer:not(:last-of-type):after,
.pagination,
.pagination > li > a,
.pagination > li > span,
.pager {
  color: var(--grey)
}
.alert a,
.alert .alert-link,
.alert .close,
.alert .close:hover,
.alert .close:focus,
.pagination > .active > a,
.pagination > .active > span,
.popover {
  color: var(--bg);
}
.alert-info {
  color: #eef;
  background-color: #245;
  border-color: #245;
}
.form-control {
  -webkit-box-shadow:inset 0 1px 1px rgba(255,255,255,0.075);
  box-shadow:inset 0 1px 1px rgba(255,255,255,0.075);
}
.form-control option {
  color: var(--lightest-grey) !important;
  background-color: var(--darker-grey) !important;
}
.form-control:focus {
  border-color: var(--light-grey);
  -webkit-box-shadow:inset 0 1px 1px rgba(255,255,255,.075), 0 0 8px rgba(154, 154, 154, 0.6);
  box-shadow:inset 0 1px 1px rgba(255,255,255,.075), 0 0 8px rgba(154, 154, 154, 0.6)
}
.form-control::-moz-placeholder,
.form-control:-ms-input-placeholder,
.form-control::-webkit-input-placeholder {
  color: var(--grey)
}
.form-control[disabled],.form-control[readonly],fieldset[disabled] .form-control {
  background-color: var(--bg);
}
.label-default {
  background-color: var(--darker-grey);
  color: var(--lighter-grey)
}
hr,
.form-control,
.progress {
  border-color: var(--dark-grey);
}
.progress,
.table .table {
  background-color: var(--darker-grey)
}
.table > thead > tr > th,
.table > tbody > tr,
.table > tbody > tr > th,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > tbody > tr > td,
.table > tfoot > tr > td,
.table > tbody + tbody,
.table > thead > tr > th,
.table-bordered,
.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td {
  border-color: var(--grey) !important;
}
.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
  background-color: var(--darker-grey)
}
.table-hover > tbody > tr > td.active:hover,
.table-hover > tbody > tr > th.active:hover,
.table-hover > tbody > tr.active:hover > td,
.table-hover > tbody > tr.active:hover > th {
  background-color: var(--darker-grey)
}
@media (max-width: 767px) {
  .table-responsive {
    border-color: var(--darker-grey);
  }
}
.infoop,
.infoop2,
.infoop2 ~ div,
.nav .open > a:hover,
.nav .open > a:focus,
.nav-pills > li.active > a.item,
.nav-pills > li.active > a.item:hover,
.nav-pills > li.active > a.item:focus {
  background-color: var(--darker-grey) !important;
}
.tstyle1 th,
.tstyle2 th,
.tstyle3 th,
.tstyle4 th,
.tstyle5 th,
.tstyle7 th,
.tstyle8 th,
.tstyle9 th {
  background-color: var(--grey);
}
.tstyle1 select,
.tstyle2 select,
.tstyle3 select,
.tstyle4 select,
.tstyle5 select,
.tstyle7 select,
.tstyle8 select,
.tstyle9 select {
  background-color: var(--darkest-grey);
  border-color: var(--dark-grey);
  -webkit-box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.075);
  box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.075);
}

.container > .title img,
.logo,
.logo-container img {
  -webkit-filter: brightness(180%) grayscale(150%);
  filter: brightness(180%) grayscale(150%);
}
.brand {
  -webkit-filter: brightness(150%);
  filter: brightness(150%);
}
input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
select,
textarea {
  border-color: var(--grey);
  background-color: var(--bg);
}
input[type=color]:focus,
input[type=date]:focus,
input[type=datetime-local]:focus,
input[type=datetime]:focus,
input[type=email]:focus,
input[type=month]:focus,
input[type=number]:focus,
input[type=password]:focus,
input[type=search]:focus,
input[type=tel]:focus,
input[type=text]:focus,
input[type=time]:focus,
input[type=url]:focus,
input[type=week]:focus,
select:focus,
textarea:focus {
  border-color: rgba(128, 128, 128, 0.9);
  -webkit-box-shadow: rgba(255, 255, 255, 0.2) 0 1px 1px inset;
  -moz-box-shadow: rgba(255, 255, 255, 0.2) 0 1px 1px inset;
  box-shadow: rgba(255, 255, 255, 0.2) 0 1px 1px inset;
}
.well {
  background-color: var(--dark-grey);
  border-color: var(--darker-grey);
  -webkit-box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.05);
  box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.05)
}
.well blockquote {
  border-color: var(--lightest-grey);
  border-color: rgba(255, 255, 255, 0.15)
}
    
.alert-warning {
  background-color: #3f291c;
  border-color: #3f291c;
  color: #fcf8e3;
}
.success0 {
  color: #e2f5e2;
  background-color: #146e14;
}
@media (min-width: 768px) {
  .btn:hover,
  .btn:focus,
  .btn-default,
  .btn-default:hover,
  .btn-default:focus,
  .btn-default:active,
  .btn-default.active,
  .open .dropdown-toggle.btn-default {
    color: var(--lighter-grey);
  }
  .btn:active,
  .btn.active {
    -webkit-box-shadow: inset 0 1px 3px rgba(255, 255, 255, 0.125);
    box-shadow: inset 0 1px 3px rgba(255, 255, 255, 0.125)
  }
  .btn-default:hover,
  .btn-default:focus,
  .btn-default:active,
  .btn-default.active,
  .open .dropdown-toggle.btn-default {
    background-color: var(--darker-grey);
  }
  .btn-default,
  .btn-default.disabled,
  .btn-default[disabled],
  fieldset[disabled] .btn-default,
  .btn-default.disabled:hover,
  .btn-default[disabled]:hover,
  fieldset[disabled] .btn-default:hover,
  .btn-default.disabled:focus,
  .btn-default[disabled]:focus,
  fieldset[disabled] .btn-default:focus,
  .btn-default.disabled:active,
  .btn-default[disabled]:active,
  fieldset[disabled] .btn-default:active,
  .btn-default.disabled.active,
  .btn-default[disabled].active,
  fieldset[disabled] .btn-default.active {
    background-color: var(--darker-grey);
    border-color: var(--dark-grey)
  }
  .btn-default .badge {
    color: var(--darker-grey) !important;
    background-color: var(--lighter-grey) !important;
  }
  .btn-primary,
  .btn-primary:hover,
  .btn-primary:focus,
  .btn-primary:active,
  .btn-primary.active,
  .open .dropdown-toggle.btn-primary {
    color: var(--darkest-grey);
  }
  .btn-primary .badge {
    background-color: var(--darkest-grey) !important;
  }
}
}
