css:
@-moz-document url("https://remote.utorrent.com/"), url-prefix("https://rapton") {
body {
    color: #66b571 !important;
}
.uki-view-ScrollPane,
.iab_ad {
    background: rgb(47, 62, 52) !important;
    color: #fff !important;
}
.categories {
    color: #fff !important;
}
#header-wrap {
    background-color: #1b1b1b !important;
}
.uki-view-VSplitPane {
    background-color: #1b1b1b !important;
}
.table-header .uki-background-CssBox {
    background: none !important;
    background-color: #3d674e !important;
}
.uki-view-Table .table-header {
    color: #fff;
}
.table-list > div > div[style*=background],
#categories [class*=selected] {
    background: #6f8a6d !important;
}
.uki-view-Label span {
    background: none !important;
}
.uki-view-ScrollPane div {
    color: #6f8a6d !important;
}
}
