/* ==UserStyle==
@name           Primavera Dark
@namespace      github.com/openstyles/stylus
@version        0.1.0
@description    A new userstyle
@author         Raeffion
==/UserStyle== */
@-moz-document url-prefix("https://flms.flipswitch.com/") {
    /*LOGIN*/
    #loginmain {
        background-color: #202020;
        -webkit-box-shadow: #969696 0 0 2px 0;
        -moz-box-shadow: #969696 0 0 2px 0;
        box-shadow: #969696 0 0 2px 0;
    }
    /*DASHBOARD*/
    #mainlayout {
        background-image: url('https://i.imgur.com/rJpZ9lm.png');
    }
    /*COURSE PLAYER*/
    /*Lesson Index Background*/
    .lessonIndexWrapper {
        background: #333;
        position: relative;
    }
    /*Background Image*/
    body {
        background-image: url('https://i.imgur.com/OLzATo8.png');
        font-weight: 400;
    }
    /*Secondary Text Color*/
    .secondary-text {
        color: #999;
    }
    /*Lesson Activity Instruction*/
    .primary-color-alt {
        color: #eee;
    }
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 14px;
        line-height: 1.428571429;
        color: #666;
        background-color: #333
    }
    .primary-bg-alt {
        background-color: #333;
    }
    .lessonactivitycontainer {
        background-color: #333;
    }
    .activityContainer {
        background-color: #333;
    }
    div.questionanswer:nth-child(odd) {
        background-color: #3f3f3f
    }
    .contentNavigationFinalized {
        background-color: #333;
    }
    .matching-def {
        border: 1px solid #666;
        background-color: #222;
    }
    .matching-def-drop {
        border-bottom: 1px solid #666;
        border-top: 1px solid #666;
        border-left: 1px solid #666;
        background: #222 !important;
    }
    .matching-term {
        border: 1px solid #666;
        background-color: #222;
    }
    .matching-term-drop {
        border-bottom: 1px solid #666;
        border-top: 1px solid #666;
        border-left: 1px solid #666;
        background: #222 !important;
    }
    .matched-answers .matching-def,
    .matched-answers .matching-term,
    .matched-answers .matching-def-drop {
        border-color: #666;
        background-color: #222;
    }
    .matching-question-hover {
        border-color: #666;
        background-color: #333;
    }
    .draggable-termcontainer:hover .matching-term {
        border-color: #666;
        background-color: #333;
    }
    .draggable-defcontainer:hover .matching-def {
        border-color: #666;
        background-color: #333;
    }
    /*ASSESSMENTS AND FEEDBACK*/
    .form-control {
        color: #999;
        background-color: #444;
        background-image: none;
        border: 1px solid #ccc;
    }
    /*THEME BLUEPRINT CSS*/
    .primary-color {
        color: #fff;
    }
    .primary-theme {
        color: #980000;
    }
    .primary-bg {
        background-color: #980000;
    }
    .secondary-bg {
        background-color: #980000;
    }
    .secondary-bg-alt {
        background-color: #333;
    }
    .secondary-color-alt {
        color: #999;
    }
    .secondary-hr {
        border-top-color: #980000;
    }
    .secondary-hr-inverse {
        border-top-color: #FFFFFF;
    }
    .btn:hover,
    .btn:focus,
    .btn.focus {
        color: #fff;
    }
    .btn-fs-primary {
        background-color: #444;
        color: #ffffff;
    }
    .btn-fs-secondary {
        background-color: #555;
        color: #3187CA;
    }
    .btn-fs-tertiary {
        color: #3187CA;
        border: 1px solid #3187CA;
        background-color: transparent;
    }
    /*Course Navigation Menu*/
    #courseNavigationMenu {
        background-color: #222;
        border-right: 1px solid #444;
    }
    #menuButton {
        background-color: #333;
    }
    #courseNavigationMenu .navex {
        background-color: #333
    }
    #courseNavigationMenu #leftnavpanel li[data-type='coursesection'] > a {
        background-color: #3f3f3f;
    }
    #courseNavigationMenu #leftnavpanel li[data-type='unit'] > a {
        background-color: #444;
        color: #999;
    }
    #courseNavigationMenu li a:hover,
    #courseNavigationMenu li a:active,
    #courseNavigationMenu li a:focus {
        text-decoration: none;
        background-color: #444
    }
    #courseNavigationMenu li.active {
        background-color: #333;
        border-top: 10px solid transparent;
        border-right: 10px solid #ccc;
        border-bottom: 10px solid transparent;
    }
    #courseNavigationMenu li.active > .chevron {
        border-top: 10px solid transparent;
        border-right: 10px solid #222;
        border-bottom: 10px solid transparent;
    }
    #courseNavigationMenu li.active > .chevron .chevron-inner {
        border-top: 9px solid transparent;
        border-right: 9px solid #333;
        border-bottom: 9px solid transparent;
    }
    /*Assessment Mode*/
    .assessment-content-container {
        background-color: #333;
    }
    .modal-body {
        background-color: #333;
    }
    .assessment-module-icon {
        background-color: #222;
        border: 1px solid #aaa;
    }
    .assessment-module-bar-description {
        background-color: #333;
    }
    /*Tables*/
    .table td,
    .table th {
        background-color: #333!important
    }
}
