}

.expanded-over {
    background: -moz-linear-gradient(top,rgba(255,255,255,0) 0,rgba(255,255,255,0) 20%,rgba(255,255,255,.9) 85%,#383838 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,rgba(255,255,255,0)),color-stop(20%,rgba(255,255,255,0)),color-stop(85%,#383838),color-stop(100%,#383838));
    background: -webkit-linear-gradient(top,rgba(255,255,255,0) 0,rgba(255,255,255,0) 20%,rgb(43, 43, 43) 85%,#383838 100%);
