    background: -webkit-linear-gradient(top,rgba(255,255,255,0) 0,rgba(255,255,255,0) 20%,rgb(43, 43, 43) 85%,#383838 100%);
    background: -ms-linear-gradient(top,rgba(255,255,255,0) 0,rgba(255,255,255,0) 20%,rgba(255,255,255,.9) 85%,#383838 100%);
