css:
@-moz-document domain(weibo.com),
domain(tw.weibo.com),
domain(overseas.weibo.com){

#midPage {
    width: 100%;
    background: url(https://s9.postimg.cc/kie5j7b33/luhan1.jpg) !important;
	background-attachment: fixed !important;
	background-repeat: no-repeat !important;
    background-size:cover !important;        
}

body, .S_page, .WB_miniblog, .WB_frame{
    width: 100%;
    background: url(https://s9.postimg.cc/kie5j7b33/luhan1.jpg) !important;
	background-attachment: fixed !important;
	background-repeat: no-repeat !important;
    background-size:cover !important;            
}

body, .FRAME_login, .B_unlog{
    background: url(https://s9.postimg.cc/kie5j7b33/luhan1.jpg) !important;
	background-attachment: fixed !important;
	background-repeat: no-repeat !important;
    background-size:cover !important;        
}

#header {
    width: 100%;
    margin: 0px auto 0px;
    background: rgba(255,255,255,0.5) !important;
}

#contTop #sideRt {
    width: 300px;
    float: right;
    padding: 12px 7px 0px 9px;
    display: none !important;
}


  .WB_global_nav .gn_header
  {
    width: 100% !important;
  }

  .WB_global_nav
  {
    height: 40px !important;
    background: #333 !important;
    opacity: 0.9 !important;
    border: none !important;
    box-shadow: none !important;
  }

  .WB_global_nav .gn_header
  {
    position: absolute !important;
    left: 70px;
    margin-top: -4px !important;
    height: 0 !important;
  }

  .WB_global_nav .gn_logo
  {
    margin: 2px 0 0 20px !important;
  }

  .WB_global_nav .gn_logo, .WB_global_nav .gn_logo .box, .WB_global_nav .gn_logo .box .logo
  {
    width: 35px !important;
    margin: 0 !important;
    background-position: 0 50% !important;
  }



  .WB_global_nav_us .gn_logo .box .logo, .WB_global_nav_us .gn_logo .box img
  {
    display: block !important;
  }

  .WB_global_nav .gn_position
  {
    position: absolute !important;
    left: 0 !important;
  }

  .WB_global_nav .gn_position .gn_nav
  {
    margin: 12px  0 0 !important;
  }

  .WB_global_nav .gn_position .gn_nav .gn_nav_list li
  {
    display: none !important;
  }

  .WB_global_nav .gn_position .gn_nav .gn_nav_list li:nth-child(5)
  {
    display: block !important;
    margin-left: 15px !important;
  }

  .WB_global_nav .gn_position .gn_nav .gn_nav_list li:nth-child(5) a em:nth-child(1)
  {
    display: none !important;
  }

  .WB_global_nav .gn_position .gn_nav .gn_nav_list li:nth-child(5) a em:nth-child(2)
  {
    color: #fff !important;
    font-size: 12px;
    max-width: 120px !important;
  }

  .WB_global_nav .gn_position .gn_login_list li a
  {
    color: #fff !important;
  }

  .WB_global_nav .gn_person
  {
    position: absolute !important;
    float: left !important;
    left: 10px;
  }

  .WB_global_nav .gn_person .S_txt1
  {
    font-size: 12px !important;
    color: #fff !important;
  }

  .WB_global_nav .W_ficon
  {
    font-size: 21px !important;
    color: #c7c7c7 !important;
    text-shadow: 0px 0px 2px #333;
  }

  .WB_global_nav .ficon_set
  {
    vertical-align: 0px !important;
  }

  .WB_global_nav .ficon_send.ficon_send
  {
    font-size: 22px !important;
  }

  .WB_global_nav .ficon_send.ficon_send, .WB_global_nav .ficon_send.ficon_send:hover
  {
    background: none !important;
  }

  .WB_global_nav .gn_set
  {
    border: none !important;
    margin: 12px 0 0 !important;
  }

	/* search */
  .WB_global_nav .gn_search_v2
  {
    width: 288px !important;
    height: 26px !important;
    position: absolute !important;
    right: 70px !important;
    margin: 11px 10px 0 0 !important;
    border: 0px !important;
    border-radius: 50px !important;
  }

  .WB_global_nav .gn_search_v2 .placeholder, .WB_global_nav .gn_search_v2 .W_input
  {
    width: 245px !important;
    height: 20px !important;
    line-height: 20px !important;
    padding: 4px 28px 4px 15px !important;
    border: none !important;
    border-radius: 50px !important;
  }

  .WB_global_nav .gn_search_v2 .ficon_search
  {
    top: 0px !important;
    text-shadow: none !important;
    color: #888 !important;
  }

  .WB_global_nav .gn_search_v2 .gn_topmenulist_search
  {
    width: 290px !important;
  }

  .WB_global_nav .gn_search_v2 .placeholder
  {
    visibility: hidden;
  }

  .WB_global_nav .gn_search_v2 .placeholder:before
  {
    content: "搜索微博、找人";
    visibility: visible;
  }

  .WB_global_nav .gn_search_v2 .W_input:focus
  {
    background: #fff !important;
    height: 18px !important;
  }

  .WB_global_nav .ficon_search
  {
    background-position: -50px -172px !important;
  }

  .WB_global_nav .gn_search
  {
    visibility: hidden;
  }

  .WB_global_nav .gn_topmenulist_tips
  {
    right: -76px !important;
    top: 32px !important;
  }

  .WB_global_nav .gn_person .W_ficon
  {
    display: none !important;
  }

	/* background */
  body, .S_page, .WB_miniblog, .WB_frame
  {
    background-image: none !important;
  }

  .FRAME_main
  {
    background-color: #eee !important;
  }


#contTop {
    width: 665px;
    margin: 8px auto 10px;
    background: rgba(255,255,255,0.5) !important;
    float: left!important;
}

#mainNavBar {
    width: 100%;
    height: 39px;
    border-top: 1px #ebebeb solid;
    background: rgba(0,0,255,0.1) !important;
}

#mainNavBar li.popPromo {
    position: relative;
    width: 210px;
    display: none !important;
}

#topBar {
    width: 100%;
    height: 31px;
    padding: 0px 0px 0px 0px;
    background: rgba(0,0,0,0.5) !important;
}





#co-header {
    width: 100%;
    margin: 0px auto 0px;
    background: rgba(255,255,255,0.5) !important;
}
.breadCrumb{display:none !important}

.weibo_nav_bg {
    background: rgba(0,0,0,0.5) !important;
}


.UG_box_l, .UG_box{
    background: rgba(255,255,255,0.5) !important;
}

#pl_unlogin_home_feed{
    background: rgba(255,255,255,0.5) !important;
	padding: 20px !important;
}

.UG_slider {
    width: 620px !important;
}





}
