css:
@-moz-document domain("github.com") {
/* Code */

.highlight .pl-k {
    color: #d5a400 !important;
}
.highlight .pl-c {
    color: #686e85;
}
.highlight .pl-s, .pl-pds, .pl-s .pl-pse .pl-s1, .pl-sr, .pl-sr .pl-cce, .pl-sr .pl-sre, .pl-sr .pl-sra, .cm-s-github-light .cm-string {
    color: #678cb1;
}
.pl-smi, .pl-s .pl-s1 {
    color: #678cb1;
}

/* function */
.highlight .pl-e, .pl-en {
    color: #678cb1;
}
.highlight .pl-c1, .pl-s .pl-v {
    color: #678cb1;
}

/* var */
.pl-v, .pl-smw {
    color: #b4b8bc;
}


/* Body Blob*/

.blob-code-inner.highlighted {
    background-color: #423B1F;
    /* #604C0F; #24292e; 50% Blend Color - https://meyerweb.com/eric/tools/color-blend/#24292E:604C0F:7:hex*/
}
.blob-code, .blob-code-inner {
    color: #b4b8bc;
}
.markdown-body blockquote {
    color: #b4b8bc;
    border-left: 0.25em solid #394046
}
.application-main, body, .Header {
    color: #9CA6A9;
    /*  background: #24292e; */
    background: #1c2126;
}
.markdown-body kbd, kbd {
    border: solid 1px #394046;
    color: #9CA6A9;
    /*  background: #24292e; */
    background: #1c2126;
}
.timeline-comment.current-user .timeline-comment-label {
    border: solid 1px #394046;
}
.blob-wrapper, .code-list .divider .blob-num, .code-list .divider .blob-code, .dropdown-menu, .select-menu-modal, .markdown-body code, .markdown-body tt, .markdown-body .highlight pre, .markdown-body pre {
    background: #24292e;
}
.cm-s-github-light .CodeMirror-lines, .cm-s-github-light .CodeMirror-gutters, .cm-s-github-light.CodeMirror {
    color: #9CA6A9;
    background: #24292e;
}
.user-profile-sticky-bar::after {
    color: #9CA6A9;
    border: none;
    background-color: #2F353C;
}

/* Header */
.timeline-comment-header, .form-group .form-control, .timeline-comment.current-user .timeline-comment-header {
    color: #9CA6A9;
    border: none;
    background-color: #22282D;
}


.file-header, .commit-tease, .user-mention, .team-mention, .commit-tease-contributors, .repohead.experiment-repo-nav, .btn, .reponav-item.selected, .social-count, .Box-row, .link-gray-dark, .Box-row--focus-gray.navigation-focus, .wiki-content, .reponav-item, .reponav-item:hover, .reponav-item:focus, .overall-summary, .numbers-summary a, .numbers-summary .nolink, .repository-lang-stats-graph, .text-emphasized, .topic-tag, .table-list-header, .js-notice, .protip, .gh-header-show, .discussion-item, .breadcrumb strong.final-path, .blob-num, .blob-num:hover, .repository-lang-stats ol.repository-lang-stats-numbers li .lang, .wiki-wrapper .wiki-auxiliary-content-no-bg, .wiki-wrapper .wiki-auxiliary-content, .filter-bar, table.files, .file-wrap, table.files td.icon .octicon-file-directory, .octicon-file, .timeline-comment-wrapper, .discussion-timeline-actions, .timeline-comment, .new-discussion-timeline .previewable-comment-form .comment-form-head.tabnav, .blankslate, .Box-header, .Box, .summary-stats li, .conversation-list-heading .inner, .conversation-list-heading, .simple-conversation-list > li, .menu-item.selected, .menu, .menu-item, .Subhead, .boxed-group-inner, .mini-repo-list-item, .btn-link, .tabnav-tab, .tabnav, .list-group-item, .list-group-item-link, .user-profile-nav, .table-list-header-toggle .btn-link, .markdown-body img, table.files tr.up-tree, .thread-subscription-status, .code-list .file-box, .code-list .code-list-item + .code-list-item, .pagination a, .pagination span, .pagination em, .pagination .gap, .pagination .disabled, .pagination .gap:hover, .pagination .disabled:hover, .markdown-format code, #footer, div.content-header, .sidebar, .pagehead, .table-list, .orghead, .org-name, .Box-row--gray, .Box-body, .MarketplaceSideNav, .drag-and-drop, .upload-enabled textarea, .pagination-loader-container, .discussion-item-ref-title .title-link, .branch-infobar, .new-discussion-timeline .closed-banner, .closed-banner, .btn:disabled, .btn.disabled, .discussion-item .author, .discussion-item-header, .discussion-item-entity, .timeline-commits .commit-message > code a, .diffbar, .pr-toolbar, .full-commit .commit-meta, .gh-header-meta, .dropdown-item, .select-menu-header, .select-menu-header .select-menu-title, .select-menu-divider, .select-menu-item, .new-discussion-timeline .previewable-comment-form .comment-body, .timeline-new-comment .previewable-comment-form .comment-body, .readme .markdown-body, .readme .plain, .boxed-group > h3, .boxed-group .heading {
    color: #9CA6A9;
    background-color: initial;
    border: none;
}
.link-gray-dark, .text-gray-dark, .text-gray {
    color: #9CA6A9 !important;
}
.bg-white, .bg-gray, .bg-gray-light {
    background-color: initial !important;
}
.markdown-body h1, .markdown-body h2, .markdown-body h3, h2, .markdown-format h2, .markdown-format h3, .markdown-format h4, .markdown-format pre.command-line, .table-list-cell, .table-list-bordered .table-list-cell:first-child, .table-list-bordered .table-list-cell:last-child {
    color: #9CA6A9;
    border: none;
}

/* Line Seperator, Border */
.subnav-item, .advanced-search-form fieldset, .mini-repo-list + .mini-repo-list > :first-child .mini-repo-list-item, .gh-header-meta, .discussion-sidebar-item + .discussion-sidebar-item, .comment-reactions.has-reactions, .discussion-item + .discussion-item, .discussion-item-review + .discussion-item, .wiki-wrapper .wiki-custom-sidebar, .wiki-rightbar .boxed-group.collapsed > h3 {
    border-color: #2F353C;
}
#footer {
    text-shadow: none;
}
.markdown-body hr, hr, .rule {
    border-bottom: 1px solid #343b42;
}

/* Table */
table.files .navigation-focus td.message a, table.files .navigation-focus td.content a, table.files tr.navigation-focus td, table.files td.content a {
    color: #9CA6A9;
    border: none;
    background-color: initial;
}
.markdown-body table tr, .markdown-body table th, .markdown-body table td {
    border: none;
    background-color: initial;
}

/* btn */
.btn-link, .ajax-pagination-form .ajax-pagination-btn, .commit-ref, .commit-ref .user {
    color: #9CA6A9;
    border: none;
    background-color: #282E33;
    border-radius: 5px;
}

/* Selected */
.markdown-body table tr:nth-child(2n), tr.navigation-focus, .menu-item.selected, .tabnav-tab.selected, .tabnav-tab:focus, .tabnav-tab:focus, .UnderlineNav-item.selected, .table-list-header-toggle .btn-link.selected, .pagination .current, .pagination .current:hover, .form-group .form-control:focus, .pagehead-tabs-item.selected, .tabnav-pr .tabnav-tab.selected, .select-menu-item.selected, .input-contrast:focus {
    color: #9CA6A9;
    background-color: #282E33;
    border-color: #e36209 transparent transparent;
}

/* Hover */
.menu-item:hover, .tabnav-tab:hover, .tabnav-tab:focus, .tabnav-tab:hover, .tabnav-tab:focus, .ajax-pagination-form .ajax-pagination-btn:hover, .ajax-pagination-form .ajax-pagination-btn:focus, .btn-link:hover, .btn:hover, .btn.hover, .list-group-item.navigation-focus, .filter-item:hover, .UnderlineNav-item:hover, .UnderlineNav-item:focus, .table-list-header-toggle .btn-link.selected:hover, .subnav-item:hover, .subnav-item:focus, .table-list-header-toggle .select-menu-button:hover, .btn:hover, .btn.hover, .pagehead-tabs-item:hover {
    color: #9CA6A9;
    text-decoration: none;
    background-color: #2F353C;
    background-image: none;
}
table.files td, table.files td .navigation-focus {
    color: #9CA6A9;
    border: none !important;
    /* background-color: #24292e;*/
    /* background-color:  #394149 !important; */
}
.form-control, .form-select {
    background-color: rgba(255, 255, 255, 0.125);
    border: none;
    color: #fff;
}
.border-right, .border-bottom, .border-left, .border-gray-light, .border, .profile-rollup-wrapper + .profile-rollup-wrapper {
    border: none !important;
}
.btn, .dropdown-divider, .markdown-body hr {
    background-image: linear-gradient(-180deg, #394149 0%, #24292e 90%);
}
.file, .border-top, .markdown-format ol > li:before, .file-navigation .get-repo-btn, .dropdown-menu::after, .timeline-comment-wrapper > .timeline-comment::before, .timeline-new-comment .timeline-comment::before, .timeline-comment-wrapper > .timeline-comment::after, .timeline-comment-wrapper > .timeline-comment::before, .timeline-new-comment .timeline-comment::after, .timeline-new-comment .timeline-comment::before, .timeline-comment-wrapper .timeline-comment.current-user::before, .timeline-comment-wrapper .timeline-comment.current-user::after, .timeline-comment-wrapper .timeline-comment.current-user::before {
    border: none !important;
}
a {
    color: hsla(212, 66%, 55%, 1);
}
a:hover {
    text-decoration: none;
}
.p-3, .py-3, .my-2 {
    margin: 0 !important;
}

/* Code Diff */
.blob-code-hunk, .blob-code-expandable {
    background-color: initial;
}
.pl-mi1 {
    color: #84A093;
    background-color: #253f31;
}
.pl-md {
    color: #A18B8F;
    background-color: #2e1e20;
}
.blob-code-addition {
    background-color: #253f31;
}
.blob-code-deletion {
    background-color: #2e1e20;
}

/* Explore */
.col-lg-9 .position-relative .position-absolute, .col-lg-9 .d-none {
    background: none !important;
}

/* Removed unsupported-browser */
.unsupported-browser {
    display: none;
}
}
