css:
@-moz-document domain(absolutewrite.com) {
	
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
}
article, aside, details, figcaption, figure, footer, header, hgroup, menu, nav, section {
    display: block;
}
body {
    line-height: 1;
}
ol, ul {
    list-style: none;
}
blockquote, q {
    quotes: none;
}
blockquote:before, blockquote:after, q:before, q:after {
    content: '';
    content: none;
}
table {
    border-collapse: collapse;
    border-spacing: 0;
}
* {
    font-family: "Inconsolata" !important;
    line-height: 1.6em !important;
    font-size: 101% !important;
}
.threadbit .nonsticky, .threadbit .discussionrow, * {
    background: none;
}
li.threadbit {
    margin-top: 12px;
    background-color: transparent !important;
    background-image: none !important;
    box-shadow: none;
    background: none;
}
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead, * {
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    background-image: none !important;
}
.threadbit, .sticky {
    background-color: transparent !important;
}
.threadbit .sticky {
    background: none;
    border: none;
    border-bottom: 1px #cacaca solid;
}
.threadbit .alt, .forumdescription {
    background: none;
    color: #cacaca;
}
.username, .online, .popupctrl, .label, .username .understate {
    color: #cacaca;
}
span.time, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    color: #cacaca;
}
dl.threadlastpost.td {
    color: #cacaca;
}
a.lastpostdate.understate img, .lastposttitle a img, img.postimg {
    opacity: 0.3;
}
.above_body div:nth-child(3) {
    display: none;
}
.above_body div:nth-child(2) p, .above_body div:nth-child(2) div {
    display: none;
}
#forums a.username, .lastposttitle a, .forumdescription a {
    color: #cacaca;
    text-decoration: underline;
}
div.username_container div.popupmenu.memberaction a.username.offline.popupctrl {
    color: black;
}
#pagetitle {
    visibility: hidden;
    display: none;
}
.breadcrumb .navbithome {
    padding: 0 0 0 0;
}
body a {
    color: rgb(0, 99, 158);
}
.forumicon {
    opacity: 0.4;
}
.breadcrumb .navbit {
    float: left;
    background-image: url(https://absolutewrite.com/forums/images/misc/navbit-arrow-right.png) !important;
    background-position: right center !important;
    background-repeat: no-repeat !important;
    padding-right: 10px;
    padding-left: 3px;
}
.above_threadlist .threadpagenav .threadpagestats {
    position: inherit;
    color: rgb(0, 0, 51);
    top: 0px;
    margin-left: -21px;
    border: 0;
    float: left;
}
.above_threadlist .threadpagenav {
    float: right;
    clear: left;
    position: absolute;
    right: 0;
    width: 60%;
    /* bottom: -10px; */
}
.above_threadlist .threadpagenav .threadpagestats, .above_threadlist .threadpagenav .pagination {
    float: right;
    clear: left;
    vertical-align: middle;
    position: relative;
    padding-right: 20px;
}
.above_threadlist {
    margin: 10px 0px 12px 0px;
    height: 1.4em;
}
.toolsmenu {
    background: none;
    width: 99%;
    display: block;
    float: left;
    clear: both;
    margin: 8px 0px 0px 7px;
    border-color: #e9f1f7;
    border-style: solid;
    border-width: 1px 0px 0px 0px;
}
.toolsmenu ul {
    padding: 0;
    float: right;
    margin-right: 0px;
}
#forumtools h6 a, #forumsearch h6 a {
    padding: 0px 6px 0px 0px;
}
.threadlist {
    margin-top: 0px;
}
body a:visited {
    color: #65b9f5;
}
.navbar_search input {
    font-size: 12px !important;
}
.globalsearch .textboxcontainer span {
    padding: 1px 1px 1px 1px;
}
}
