css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fiuxy.bz")
{
.nodeList .categoryStrip {
    font-size: 11px;
    color: rgb(200, 200, 200);
    padding: 0px 0px;
    margin: 3px auto 0;
    margin: 8px 0 13px;
    min-height: 6px;
}
.node .nodeText .nodeTitle {
    font-size: 20pt;
}
.nodeList .node.level_2 {
    float: left;
    width: 23%;
}
.node .nodeText .nodeTitle {
    font-size: 10pt;
}
.nodeStats dl,
.subForumsPopup {
    display: none;
}
.node .nodeInfo.primaryContent,
.node .nodeInfo.secondaryContent {
    min-height: 58px;
    border: none;
}
.node .nodeLastPost .lastThreadTitle,
.node .nodeLastPost .lastThreadMeta {
    display: none;
}
.nodeList .node.level_2 .nodeLastPost {
    display: none;
}
.categoryIcon {
    display: none;
}
.nodeImg {
    display: none;
}
.sidebar {
    display: none !important;
}
.mainContent {
    margin-right: 0px;
}
}
