css:
@-moz-document domain("korben.info") {
body {
    background: #2a2a2d;
}

.app-aside {
    background: #191919;
}

.entry-container {
    background-color: #2a2a2d;
}

.wp-block-code {
    background: #515151;
}

code {
    background-color: #515151;
    color: #ffffff !important;
}

p {
    color: #aaaab6;
    text-align : justify;
}

.entry-container::after, .entry-container::before {
    background: linear-gradient(180deg,#fff,#2a2a2d);
}

.entry-container a:hover {
    color: #CCE2FE !important;
}

.app-container a:hover {
    color: #CCE2FE !important;
}

.app-aside .widget-action:nth-of-type(2n) {
    color: #aaaab6 !important;
    background: #3B4C62;
}

.app-aside .widget-action:nth-of-type(2n) .lead {
    color: #aaaab6;
}

.widget-discover {
    background-color: #646464;
}

.comment-author, .link-content, h1, h2, h3, h4, h5, h6 {
    color : #aaaab6;
}

li {
    color: #aaaab6;
}

.app-extra {
    background-color: #1A1A1A;
}

.widget-discover {
    background-color: #0D0D0D !important;
}

.comment-respond {
    background: #464646;
}

.entry-format a[rel="next"], .entry-format a[rel="prev"] {
    background-color: #464646;
}

.entry-format a[rel="next"]:hover, .entry-format a[rel="prev"]:hover {
    background-color: #6F6F6F;
}

td {
    color: #aaaab6;
}
blockquote p{
    color: #aaaab6;
}

svg {
    color: #fff !important;
}

div {
    color: #aaaab6;
}
}
