css:
@-moz-document domain("www.acfun.cn") {
  
body {
    font-family: "AcFun Symbol","Helvetica Neue",Helvetica,Arial,"pingfang SC","Microsoft Yahei",STHeiti,sans-serif;
    font-size: 12px;
    color: #666;
    line-height: 1.6;
    background: url(https://s20.postimg.cc/s3tqbkon1/dirlaba.jpg) !important;
    background-repeat:no-repeat !important;
    background-size:100% !important;
    background-attachment:fixed !important;
    background-position: 0px -100px !important;
    
}

.header .header-banner .banner-href {
    background: none !important;
}

.header {
    background: transparent !important;
}

.main{background:none !important;}

#main > section{
    background: rgba(255,255,255,0.8) !important;
    padding:10px !important;
}





.header-pendant {
    width: 222px;
    height: 180px;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 1;
    background:none !important;
}


.header .nav {
    top: 180px;
    z-index: 9;
    height: 46px;
    line-height: 46px;
    font-size: 14px;
    position: relative;
    width: 100%;
    border-bottom: 0px !important;
    background: transparent !important;
}

.header .nav .nav-parent {
    height: 46px;
    background: rgba(255,255,255,0.8) !important;
    padding: 0px !important;
}

.header .header-top {
    background: rgba(255,255,255,0.8) !important;
}

  
}
