css:
@-moz-document domain("youtube.com") {
:root {
	--ytd-masthead-height: 45px;
}
#container.ytd-masthead {
	height: var(--ytd-masthead-height);
}
a.yt-simple-endpoint.yt-formatted-string {
	color: #6DA741;
}
#masthead {
	background-color: #636363;
}
#search-form {
	background-color: #4a4a4a;
}
#search-form #container {
	background-color: #4a4a4a;
	border-color: #383838;
}
#search-icon-legacy g {
	fill: #73BA25;
}
.ytd-page-manager {
	background-color: #3a3a3a;
}
.style-default-active g.yt-icon {
	fill: #73BA25;
}
.style-text g.yt-icon {
	fill: #6DA741c0;
}
g.yt-icon {
	fill: #6DA741;
}
#toggleButton {
	position: relative;
	top: 1px;
	left: 1px;
}
.toggle-container.paper-toggle-button {
	height: 20px;
	width: 45px;
}
.toggle-button.paper-toggle-button {
	height: 18px;
	width: 18px;
	background-color: #202020;
}
.toggle-bar.paper-toggle-button {
	border-radius: 100px;
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
	background-color: #202020;
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
	background-color: #73ba25;
}
paper-toggle-button[checked] .toggle-button.paper-toggle-button {
	transform: translate(25px, 0);
}
.guide-content {
	background-color: #202020;
}
.style-scope .ytd-guide-renderer {
	background: #202020;
}
#like-bar.ytd-sentiment-bar-renderer {
	background: #73ba25;
}
}
