css:
@-moz-document domain("app.asana.com") {
/* >> TO BE REVIEWED << */
.topbarContingentUpgradeButton-button:not(.Button--disabled) {
    display: none;
}
.sidebar-mountNode.is-collapsed {
    margin-left: 0;
}
.SidebarReports {
    display: none;
}
.SidebarRecentsAndFavoritesCollapsible-showMoreOrLessText {
    display: none;
}
.SidebarCollapsibleHeader-zipIcon {
    display: none;
}
.SidebarCollapsibleHeader-name {
    margin-left: 15px;
}
.SidebarTeamMembership-contents > .NavigationLink {
    display: none;
}
.SidebarTeamMembersList {
    display: none;
}
.SidebarTeamMembership.is-workspace {
    margin-top: -15px;
    padding-top: 0;
}
.SidebarItemRow {
    height: 24px;
    line-height: 24px;
}
.asanaView-paneGutter {
    display: none;
}
.asanaView-body {
    padding-top: 0 !important;
    margin: 5px;
}
.lunaui-grid-center-pane-container#center_pane_container {
    max-width: none;
}
.ProjectOverview {
    margin-left: 0 !important;
}
.ProjectOverview-descriptionSection {
    margin-bottom: 5px;
}
.ProjectOverviewDescription-seeMoreLink {
    display: none;
}
.ProjectOverviewDescription-textEditor .ql-editor {
    max-height: none!important;
    font-size: 12px;
    line-height: 16px;
}
.SidebarCollapsibleHeader-name {
    margin-left: 15px;
    border-bottom: none!important;
    text-transform: uppercase;
    font-size: 12px!important;
    font-weight: 500;
    padding-bottom: 0!important;
}
.topbarSettingsMenu-upgrade {
    display: none;
}
.topbarHelpMenuButton-textButton {
    margin-right: 0!important;
}
.SidebarHeader-closeIcon {
    display: none;
}

.SidebarHeader-asanaLogo {
    visibility: hidden;
}
.topbarHelpMenuButton-textButton {
    display: none;
}
.ProjectOverview-statusUpdateSection {
    margin-top: 5px;
    padding: 0;
}
.Tab {
    padding-bottom: 11px;
}
.SidebarRecentsAndFavorites {
    padding-bottom: 10px;
}
.SidebarRecentsAndFavorites-showMoreOrLessButton {
    display: none;
}
a.ExternalLink.TopbarAppsPageLink {
	display: none;
}

.ProjectDropdownMenuButton-downIcon,
.ProjectDropdownMenuButton-moreIcon {
    margin-top: 4px;
}
.TokenButton.TokenButton--interactive:hover {
    border-style: none;
}
.StatusUpdateDisplay-colorStatusInBody {
    display: none;
}
.RichText {
    font-size: 12px;
    line-height: 16px;
}
.ColorStatus {
    height: 40px;
}
.ProjectOverview-statusUpdateSection .ProjectOverviewSection-header {
    z-index: 2;
    background: none;
    color: #FFF;
    font-weight: 700;
}
.ProjectOverview {
    margin-right: 2px;
}
.DeprecatedAsanaPageView-paneGutter {
    flex: 1 0 15px;
}
.lunaui-grid-center-pane-container #center_pane,
#right_pane_container #right_pane {
    border-radius: 5px !important;
    overflow: hidden;
    margin-bottom: 12px;
}

/* CONTENT */

.DeprecatedAsanaPageView-paneGutter {
    display: none;
}
.DeprecatedAsanaPageView-body {
    padding: 5px !important;
}
#center_pane_container, #right_pane_container {
    margin: 5px !important;
}
#center_pane, #right_pane {
    margin: 0px !important;
}
.ProjectOverviewSection-content {
	margin: 0 15px 10px 15px;
}


/* >> MAIN PAGE HEADER  << */

.DeprecatedAsanaPageView-pageHeader {
    z-index: 99999;
    width: 85%;
    position: absolute;
    top: 0;
}

.PageHeaderCollapsedStructure {
    height: 40px;
    box-shadow: none;
    padding: 0 5px;
}

.Topbar-accountInfo {
	z-index: 100000;
	max-width: 160px;
}

/* >> SCROLLBAR << */

::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #F5F5F5;
}
::-webkit-scrollbar {
    width: 10px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
    background-color: #000000;
    border: 2px solid #555555;
}

/* SaM */
#bg_pattern {
    background-image: url(https://storage.googleapis.com/sam-file/admin/images/background.jpg) !important;
    background-size: cover;
}
.Sidebar.Sidebar--withClarifiedNavigation {
    background: rgba(0, 0, 0, 0.5);
}
.GridHeader--isCompactMode.GridHeader, .GridHeader--isCompactMode.PotGridPlaceholder-gridHeader, .GridHeader, .SingleTaskPaneToolbar, .ProjectOverviewSection-header {
    background: #EEEEEE;
    color: #000;
    height: 39px;
    padding-bottom: 0;
    border-bottom: 1px solid #E0E0E0;
}
.ProjectOverview-descriptionSection {
    margin: 0;
    padding: 0;
}
.ProjectOverviewSection-header {
    line-height: 40px;
    padding-left: 15px;
}
.GridHeader .Button:hover {
    background: #616161;
    color: #EEEEEE;
}
.GridHeader .Button, .TokenButton {
    background: transparent;
    color: #000;
    opacity: 1;
    border: none;
}

/*
 * NEW
 */

/* Search bar */
.TopbarSearchTypeahead.TopbarSearchTypeahead--withClarifiedNavigation {
    position: fixed!important;
    opacity: 0.8;
    left: -46px;
    top: 10px;
    height: 30px;
}
.TopbarSearchTypeahead:hover,
.TopbarSearchTypeahead:active,
.TopbarSearchTypeahead:focus {
    opacity: 1;
}
.TopbarSearchTypeahead--withClarifiedNavigation .TopbarSearchTypeahead-background {
    background: none;
    margin-left: 0;
}
.TopbarSearchTypeahead--withClarifiedNavigation .TopbarSearchTypeahead-background::before {
    background: none;
}
.TopbarSearchTypeahead--withClarifiedNavigation .TopbarSearchTypeahead-input:not(:focus),
.TopbarSearchTypeahead--withClarifiedNavigation .TopbarSearchTypeahead-input:focus {
    width: 224px;
}
.LayerPositioner.LayerPositioner--left, .LayerPositioner.LayerPositioner--alignRight {
    -webkit-justify-content: flex-start;
    justify-content: flex-start;
    margin-left: 0;
    left: 10px !important;
}

/* Topbar */
.TopbarPageHeaderStructure-navMenuRow {
    margin-left: 300px;
    position: fixed;
}
.TopbarPageHeaderStructure-navMenuRow, .TopbarPageHeaderStructure, .TopbarPageHeaderPlaceholder {
    height: 50px;
}
.pageTopbarView-pageHeader {
    max-width: 290px;
}

/* SubTask completed */
.SubtaskTaskRow--completed {
    display: none;
}
}
