css:
@-moz-document regexp("^.*elmah.axd.*$") {
body,
.odd-row {
    background: #303030;
}
th,
td,
#SpeedList {
    border: none;
}
#ErrorDetail,
.even-row {
    background: #202020;
}
#SpeedList {
    background: none;
}
#SpeedList a:hover {
    background: #111;
    color: inherit;
}
a:visited {
    color: #f10078;
}
a,
h1 {
    color: #00cff3;
}
th {
    background: #606060;
}
.table-caption {
    background: #404040;
}
.st-type,
.st-param-type {
    color: #7575f3;
}
body,
.st-param-name {
    color: #999;
}
.st-file,
.st-line {
    color: #d211d2;
}
.st-method {
    color: #00b5b5;
}
table {
    box-shadow: 5px 5px 5px -1px #222;
}
/*Scrollbars*/

::-webkit-scrollbar {
    width: 8px;
}
::-webkit-scrollbar-track {
    background: transparent;
}
::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, .1);
    border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, .25);
}
::-webkit-scrollbar-thumb:active {
    background: rgba(255, 255, 255, .5);
}
}
