css:
@-moz-document regexp("http(.*)://(www|boards|find|sys)\.4chan\.org/(.*)"), regexp("http(.*)://(www|boards|find|sys)\.4channel\.org/(.*)"), regexp("http(.*)://i\.4cdn\.org/(.*)/(.*)\.(?!gif|jpg|jpeg|png|webm).*") {

/* General */
html, body {background: none !important; background-color: #ECECEC !important;}
hr:not(#unread-line) {background: #000000 !important; border-color: #000000 !important;}
img {color: #000000;}
.box-outer {background-color: #DBDBDB !important; border-color: #000000;}
.boxbar {background-color: #C4C4C4; border-bottom: 1px solid #000000;}
.boxcontent {background-color: #DBDBDB;}
footer, frameset {border-color: #000000;}
footer li {background-color: #DBDBDB; border-color: #000000 !important;}
fieldset {border: 1px solid #000000;}
input:focus, textarea:focus {border-color: #000000 !important;}
#globalMessage {color: red !important}

/* Front page */
#announce .boxbar {background-color: #C4C4C4; color: #800;}
#ft li.fill, #ft ul {background-color: #ECECEC; border-color: #000000;}
#ft li.first, #ft li, #fp-menu, #fp-menu > ul > li.fp-menu-sep {background-color: #DBDBDB; border-color: #000000;}
.c-thread img, #disclaimer-dialog {border-color: #000000;}
#disclaimer-dialog .hd {background-color: #C4C4C4; color: #800; border-bottom: 1px solid #000000;}
#disclaimer-dialog .bd {background-color: #DBDBDB;}
#fp-menu li:hover {background-color: #C4C4C4;}
body > ul:nth-child(2) > h2:nth-child(1), body > h2:nth-child(3), body > h2:nth-child(5), body > h2:nth-child(7) {background-color: #C4C4C4; border-color: #000000;}

/* Index */
div.pagelist {background-color: #DBDBDB; border: 1px solid #000000; height: 23px;}
div.cataloglink {border-color: #000000 !important;}

/* Catalog */
#watchList li:first-child {border-color: #000000;}

/* Thread */
.reply:target {background-color: #C4C4C4 !important; border: 1px solid #000000 !important;}
.postblock {background-color: #C4C4C4 !important; border-color: #000000;}

/* Index & Thread shared */
div.reply, .preview {background-color: #DBDBDB; border: 1px solid #000000 !important;}
.reply.highlight {background-color: #C4C4C4 !important; border: 1px solid #000000 !important;}
div.sideArrows {color: #000000;}
table.postForm > tbody > tr > td:first-child {background-color: #C4C4C4; border-color: #000000;}
.yotsuba_b_new #watchList li:first-child, .yotsuba_new #watchList li:first-child {border-color: #000000;}

/* Index, Catalog, and Thread shared */
.dd-menu ul {background-color: #DBDBDB !important; border: 1px solid #000000; border-bottom: none;}
.dd-menu li {border-bottom: 1px solid #000000;}
.dd-menu li:hover {background-color: #ECECEC;}
#threadWatcher {background-color: #DBDBDB; border: 1px solid #000000; border-color: #000000;}
.panel {background-color: #DBDBDB; border: 1px solid #000000;}
.panelHeader {border-color: #000000 !important;}
.persistentNav, div#boardNavMobile {background-color: #DBDBDB; border-bottom: 1px solid #000000;}
.yotsuba_new #atHeader, .yotsuba_b_new #atHeader {border-bottom: 1px solid #000000;}
.yotsuba_new .click-me, .yotsuba_b_new .click-me, .futaba_new .click-me, .burichan_new .click-me, .tomorrow .click-me, .photon .click-me {background-color: #DBDBDB; border-color: #000000;}
.yotsuba_new .click-me:before, .yotsuba_b_new .click-me:before, .futaba_new .click-me:before, .burichan_new .click-me:before, .tomorrow .click-me:before, .photon .click-me:before {border-color: #000000 transparent;}
.yotsuba_new .click-me:after, .yotsuba_b_new .click-me:after, .futaba_new .click-me:after, .burichan_new .click-me:after, .tomorrow .click-me:after, .photon .click-me:after {border-color: #DBDBDB transparent;}

/* Archive */
#arc-list > thead > tr > td:nth-child(1), #arc-list > thead > tr > td:nth-child(2), #arc-list > thead > tr > td:nth-child(3) {background-color: #C4C4C4 !important;}
table.flashListing tr:nth-of-type(odd) {background-color: #DBDBDB;}

/* Donate page */
#donate-form {background-color: #DBDBDB; border-color: #000000;}
#field-amount {border: 1px solid #000000;}

/* Bans page */
table#log-entries {border-color: #000000;}
#log-entries th {background-color: #C4C4C4; border-color: #000000;}
#log-entries td, #log-entries tr {background-color: #DBDBDB; border-color: #000000;}
#quote-preview.ws, #quote-preview.nws {background-color: #DBDBDB; border-color: #000000; border-width: 1px;}

/* Blotter & Feedback pages shared */
table#entries {border-color: #000000;}
#entries th {background-color: #C4C4C4; border-color: #000000;}
#entries td, #entries tr {background-color: #DBDBDB; border-color: #000000;}

/* FAQ & Pass pages shared */
.boxcontent dd {border-color: #000000;}

/* FAQ, Rules, & Pass pages shared */
.left-box .boxbar {background: #C4C4C4;}
.right-box .boxbar {background: #C4C4C4; color: #59a;}

/* News page */
.content h3 {background-color: #ECECEC; border-color: #000000;}

/* Press page */
.bluehr {background-color: #000000;}

/* Mobile View */
#boardNavMobile.mobile {background-color: #DBDBDB; border-bottom: 1px solid #000000;}
.mobileib.button, .mobilePostFormToggle.mobile.hidden.button, .postLink.mobile > a, .mobilePostFormToggle.button, .button.m-depagelink {background-image: none !important; background-color: #DBDBDB !important; border-color: #000000 !important;}
@media only screen and (max-width: 480px) {.yotsuba_new .mobilebtn .btn-wrap, .futaba_new .mobilebtn .btn-wrap, .yotsuba_b_new .mobilebtn .btn-wrap, .burichan_new .mobilebtn .btn-wrap, .button {background-image: none; background-color: #DBDBDB; border-color: #000000;}}

/* Commented out for conflict with 4chan X - Will maybe find a solution at a later date /* .postContainer.opContainer {background-color: #DBDBDB;} */
.post.reply {background-color: #DBDBDB; border-color: #000000 !important;}
.postInfoM.mobile, .postLink.mobile, .backlink.mobile {background-color: #C4C4C4 !important; border-color: #000000 !important;}

/* 4chan X */
:root.yotsuba .dialog, :root.yotsuba-b .dialog, :root.yotsuba .inline, :root.yotsuba-b .inline, :root.yotsuba.catalog-hover-expand .catalog-container:hover > .post, :root.yotsuba-b.catalog-hover-expand .catalog-container:hover > .post {background-color: #DBDBDB; border-color: #000000;}
:root.yotsuba #fourchanx-settings fieldset, :root.yotsuba .section-main div::before, :root.yotsuba-b #fourchanx-settings fieldset, :root.yotsuba-b .section-main div::before, :root.yotsuba.catalog-hover-expand .catalog-container:hover .catalog-reply, :root.yotsuba-b.catalog-hover-expand .catalog-container:hover .catalog-reply, :root:not(.werkTyme) .catalog-thread.watched .catalog-thumb, #qr {border-color: #000000;}
:root.yotsuba .suboption-list > div:last-of-type, :root.yotsuba-b .suboption-list > div:last-of-type, :root.yotsuba #header-bar.dialog, :root.yotsuba-b #header-bar.dialog {background-color: #DBDBDB;}
:root.yotsuba .qr-link, :root.yotsuba-b .qr-link {background: none; background-color: #C4C4C4; border-color: #000000;}
:root.yotsuba .qr-link:hover, :root.yotsuba-b .qr-link:hover {background-color: #C4C4C4;}
.qphl {outline-color: #C4C4C4;}
:root.yotsuba-b .focused.entry {background: #ECECEC}
#menu, .submenu {padding-top: 0px; padding-bottom: 0px;}

}

@-moz-document regexp("http(.*)://sys\.4chan\.org/(.*)/(imgboard|a(.*)).*"), regexp("http(.*)://sys\.4channel\.org/(.*)/(imgboard|a(.*)).*") {

/* Background color for reports window */
html, body {background-color: #DBDBDB !important;}

}

@-moz-document regexp("http(.*)://(www|boards|find|sys)\.4chan\.org/(.*)"), regexp("http(.*)://(www|boards|find|sys)\.4channel\.org/(.*)") {

/* New posts marker: changes the color of the line that appears above unread posts */
.newPostsMarker:not(#quote-preview) {box-shadow: 0 3px #000000;}
/* 4chan X specific */
#unread-line {border-color: #000000;}

/* Changes highlight color and disables focus color */
::selection {background-color:#777777; color:#FFFFFF;}
:focus {outline: none;}

/* Changes text field autofill color */
:-webkit-autofill {-webkit-box-shadow: 0 0 0px 20px #DBDBDB inset;}

/* Horizontal rules: Hides those that are no longer necessary when ads are not displayed - There is an additional entry in a section below */
#delform > hr:nth-child(5), #delform > hr:nth-child(7), #delform > hr, div.adg-rects.desktop {display: none;}
/* 4chan X specific */
#delform > hr:nth-child(4) {display: none;}

/* Box shadows: Removed for all 4chan elements that have them & adds a border to deleted images in the catalog - Box shadow edits for CAPTCHA can be found in a section below */
#fp-menu, #fp-menu > ul > li.fp-menu-sep, .thumb {box-shadow: none;}
.thumb.imgdel:not(.hl) {border: 1px solid #000000;}
/* 4chan X specific */
:root.yotsuba #header-bar.dialog, :root.yotsuba-b #header-bar.dialog, #thread-watcher, .dialog:not(#qr):not(#thread-watcher):not(#header-bar), .catalog-thumb {box-shadow: none;}
.catalog-thumb.deleted-file {border: 1px solid #000000;}
.catalog-thread.filter-highlight .catalog-thumb {border: none;}

/* Border radius - Sets to 0 for all elements that have a border radius greater than 0 - Border radius edits for CAPTCHA can be found in a section below */
/* Mobile View specific */
@media only screen and (max-width: 480px) {.yotsuba_new .mobilebtn .btn-wrap, .futaba_new .mobilebtn .btn-wrap, .yotsuba_b_new .mobilebtn .btn-wrap, .burichan_new .mobilebtn .btn-wrap, .button {border-radius: 0;}}
/* 4chan X specific */
#qr, .catalog-thumb, #menu, .submenu, #qr-file-button, #file-n-submit input, .qr-link {border-radius: 0;}

/* Select menus & text/input fields: Changes border color (when and when not in focus) and slight size/padding changes */
input[type=text], input[type=text]:focus, input[type=password], input[type=password]:focus, select, textarea, textarea:focus, input[type="text"], input[type="text"]:focus {border: 1px solid #000000 !important;}
input[type=text]:not(#search-field):not(#field-email):not(#field-email2):not(#field-name), input[type="text"]:not(#search-field):not(#field-email):not(#field-email2):not(#field-name), input[type=password] {padding-bottom: 2px !important;}
/* 4chan X specific */
#index-size, #index-mode {margin-left: 1px;}
.field, .field:hover, input:not([type]):focus {border-color: #000000 !important;}
input.field:nth-child(3) {margin: 1px 1px 1px 0px;}
input.field:nth-child(4) {margin: 1px 0px 1px 0px;}
input.field:nth-child(5) {margin: 1px 0px 1px 1px;}
#qr-filename-container {margin: 0px 1px 0px 1px;}
#qr textarea {min-width: 302px;}
/* Search page */
#search-field {height: 19px;}
#js-sf-bf {height: 21px; padding-bottom: 2px}
/* Donate page */
#field-email, #field-email2, #field-name {padding-bottom: 1px;}
#field-amount {padding-top: 2px; padding-bottom: 2px;}

/* Buttons: Changes colors and adds borders to those that do not have them */
button, input[type="button"], input[type=submit] {background: #C4C4C4; border: 1px solid #000000; min-height: 21px;}
#bottomReportBtn {margin-left: 1px;}
.prev, .next {transform: translateY(1px);}
/* Mobile View specific */
@media only screen and (max-width: 480px) {#postForm input[type="submit"] {margin-left: 1px;}}
/* 4chan X specific */
#qr-file-button, #file-n-submit input {background: #C4C4C4; border-color: #000000;}

/* Checkboxes */
input[type=checkbox] {-webkit-appearance: none; height: 12px; width: 12px; margin: 2px 5px 2px 3px; background-color: #C4C4C4; border: 1px solid #000000;}
input[type="checkbox"]:checked:before {content: '\02FE'; color: #000000; font-size: 24px; position: absolute; transform: translateY(-17.5px) translateX(-7.5px) rotate(-45deg);}
input[type="checkbox"]:disabled {opacity: 0.11;}
.deleteform > input[type="checkbox"], #postForm input[type="checkbox"], #qrSpoiler input[type="checkbox"], #settingsMenu input[type="checkbox"], .panel input[type=checkbox], .postInfo.desktop input[type=checkbox] {transform: translateY(2px);}
/* Radio buttons */
input[type=radio] {-webkit-appearance: none; -moz-appearance: none; height: 12px; width: 12px; margin: 2px 4px 2px 5px; background-color: #C4C4C4; border: 1px solid #000000; border-radius: 100%;}
input[type="radio"]:checked {background: radial-gradient(circle, black 35%, #C4C4C4 50%);}
input[type="radio"]:disabled {opacity: 0.11;}
/* 4chan X*/
#fourchanx-settings input[type="checkbox"] {transform: translateY(2px);}
.suboption-list > div::before {height: .75em;}

/* Gives "No file chosen" in the Quick Reply window enough space to be displayed completely and other adjustments to account for that */
#qrForm > div:nth-child(1) > input[type="text"], #qrEmail, #quickReply textarea {min-width: 333px;}
#qrFile {width: 176px;}
#qrSpoiler {margin-right: 6px;}

/* Thread statistics: Adds additional "Replies," "Images," "Posters," and "Page" text */
.ts-replies:after {content: " Replies";}
.ts-images:after {content: " Images";}
.ts-ips:after {content: " Posters";}
.ts-page:before {content: "Page ";}

/* Changes the "Return" button to say "Index" instead */
.navLinks.mobilebtn > span:first-of-type > a, .navLinksBottom.mobilebtn > span:first-of-type > a, .navLinks.desktop > a:first-of-type, .navLinksBot.desktop > a:first-of-type {font-size: 0;}
.navLinks.mobilebtn > span:first-of-type > a::after, .navLinksBottom.mobilebtn > span:first-of-type > a::after, .navLinks.desktop > a:first-of-type::after, .navLinksBot.desktop > a:first-of-type::after {content: "Index"; font-size: 10pt;}

/* Makes it so filenames are no longer truncated with "(...)" */
.fileText > a[title] {font-size: 0;}
.fileText > a[title]::after {content: attr(title); font-size: 10pt;}
.fileText {white-space: nowrap; max-width: none;}

/* Consistent spacing between horizontal rules and vertical centering of Thread/Catalog elements between them */
hr:not(#unread-line) {margin-top: 6px; margin-bottom: 5px;}
.navLinks.desktop {transform: translateY(1px);}
.extButton.wbtn {margin-top: -2px;}
.navLinks.desktop > label > input[type="checkbox"] {margin-top: -1px; margin-bottom: 0px;}
#search-box {margin-top: -1px; margin-bottom: -4px; transform: translateY(-1px);}
#settings {margin-top: -1px; margin-bottom: -3px;}

/* Decreases the top margin of the information above a post slightly */
.postInfo.desktop {margin-top: -2px;}

}

