/* CAPTCHA */
.rc-anchor-light.rc-anchor-normal {border-color: #000000; border-radius: 0;}
.rc-anchor {-webkit-box-shadow: none; box-shadow: none;}
#rc-imageselect {background-color: #DBDBDB;}
.rc-imageselect-desc-wrapper {background-color: #C4C4C4; border: 1px solid #000000; color: #4A90E2;}
